      // Enforce Reception 14-day history limit
      if (!enforceReceptionDateLimit(req.user, date as string)) {
        return res.status(403).json({ 
          error: { 
            code: "FORBIDDEN", 
            message: "Reception users can only view bookings from the last 14 days" 
          } 
