import { Switch, Route, Redirect, useLocation } from "wouter";
import { QueryClientProvider } from "@tanstack/react-query";
import { queryClient } from "./lib/queryClient";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/lib/theme-provider";
import { AuthProvider, useAuth } from "@/lib/auth-context";
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { AppSidebar } from "@/components/app-sidebar";
import { ThemeToggle } from "@/components/theme-toggle";
import { Footer } from "@/components/footer";

// Auth pages
import Login from "@/pages/login";
import Register from "@/pages/register";
import ResetPassword from "@/pages/reset-password";
import ChangePassword from "@/pages/change-password";

// Client pages
import Hostesses from "@/pages/hostesses";
import HostessProfile from "@/pages/hostess-profile";
import MyBookings from "@/pages/my-bookings";
import ClientMessages from "@/pages/client-messages";

// Admin pages
import AdminDashboard from "@/pages/admin-dashboard";
import AdminCalendar from "@/pages/admin-calendar";
import AdminServices from "@/pages/admin-services";
import AdminAnalytics from "@/pages/admin-analytics";
import AdminImport from "@/pages/admin-import";
import AdminExport from "@/pages/admin-export";
import AdminUsers from "@/pages/admin-users";
import AdminClients from "@/pages/admin-clients";
import AdminHostesses from "@/pages/admin-hostesses";
import AdminHostessImport from "@/pages/admin-hostess-import";
import AdminClientImport from "@/pages/admin-client-import";
import AdminCSVSplitter from "@/pages/admin-csv-splitter";
import AdminPhotoApprovals from "@/pages/admin-photo-approvals";
import AdminUpcomingSchedule from "@/pages/admin-upcoming-schedule";
import AdminTriggerWords from "@/pages/admin-trigger-words";
import AdminFlaggedConversations from "@/pages/admin-flagged-conversations";
import ClientUpcomingSchedule from "@/pages/client-upcoming-schedule";

// Reception pages
import ReceptionCalendar from "@/pages/reception-calendar";
import ReceptionWeekly from "@/pages/reception-weekly";

// Staff pages
import StaffDashboard from "@/pages/staff-dashboard";
import StaffSchedule from "@/pages/staff-schedule";
import StaffMessages from "@/pages/staff-messages";

// Shared
import NotFound from "@/pages/not-found";
import Home from "@/pages/home";

function ProtectedRoute({ children, allowedRoles }: { children: React.ReactNode; allowedRoles?: string[] }) {
  const { user, isLoading } = useAuth();
  const [location] = useLocation();

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return <Redirect to="/login" />;
  }

  // Redirect to change password if required, but not if already on that page
  if (user.forcePasswordReset && location !== "/change-password") {
    return <Redirect to="/change-password" />;
  }

  if (allowedRoles && !allowedRoles.includes(user.role)) {
    return <Redirect to="/" />;
  }

  return <>{children}</>;
}

function AppRouter() {
  const { user, isLoading } = useAuth();
  const [location] = useLocation();

  // Determine default route based on user role
  const getDefaultRoute = () => {
    if (!user) return "/hostesses";

    switch (user.role) {
      case "ADMIN":
        return "/admin/dashboard";
      case "RECEPTION":
        return "/reception/calendar";
      case "STAFF":
        return "/staff/dashboard";
      default:
        return "/hostesses";
    }
  };

  const sidebarStyle = {
    "--sidebar-width": "20rem",
    "--sidebar-width-icon": "4rem",
  } as React.CSSProperties;

  const needsSidebar = user && (
    ["/admin", "/reception", "/staff"].some(path => location.startsWith(path)) ||
    location.startsWith("/hostesses") ||
    location.startsWith("/hostess/") ||
    location.startsWith("/bookings") ||
    location.startsWith("/my-bookings") ||
    location.startsWith("/upcoming-schedule") ||
    location.startsWith("/messages")
  );

  // Show loading state if we're on a protected route and auth is still loading
  if (isLoading && ["/admin", "/reception", "/staff"].some(path => location.startsWith(path))) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (needsSidebar) {
    return (
      <SidebarProvider style={sidebarStyle}>
        <div className="flex h-screen w-full">
          <div className="relative z-50">
            <AppSidebar />
          </div>
          <div className="flex flex-col flex-1">
            <header className="flex items-center justify-between p-3 border-b bg-card">
              <SidebarTrigger data-testid="button-sidebar-toggle" />
              <ThemeToggle />
            </header>
            <main className="flex-1 overflow-auto">
              <Switch>
                <Route path="/">
                  <Redirect to={getDefaultRoute()} />
                </Route>

                {/* Admin routes */}
                <Route path="/admin/dashboard">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminDashboard />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/calendar">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminCalendar />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/weekly">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <ReceptionWeekly />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/services">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminServices />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/analytics">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminAnalytics />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/users">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminUsers />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/clients">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminClients />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/hostesses">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminHostesses />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/hostess-import">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminHostessImport />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/client-import">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminClientImport />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/csv-splitter">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminCSVSplitter />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/import">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminImport />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/export">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminExport />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/photo-approvals">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminPhotoApprovals />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/upcoming-schedule">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION"]}>
                    <AdminUpcomingSchedule />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/trigger-words">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminTriggerWords />
                  </ProtectedRoute>
                </Route>
                <Route path="/admin/flagged-conversations">
                  <ProtectedRoute allowedRoles={["ADMIN"]}>
                    <AdminFlaggedConversations />
                  </ProtectedRoute>
                </Route>

                {/* Reception routes */}
                <Route path="/reception/calendar">
                  <ProtectedRoute allowedRoles={["RECEPTION"]}>
                    <ReceptionCalendar />
                  </ProtectedRoute>
                </Route>
                <Route path="/hostesses">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION", "CLIENT"]}>
                    <Hostesses />
                  </ProtectedRoute>
                </Route>
                <Route path="/hostess/:slug">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION", "CLIENT"]}>
                    <HostessProfile />
                  </ProtectedRoute>
                </Route>
                <Route path="/upcoming-schedule">
                  <ProtectedRoute allowedRoles={["ADMIN", "RECEPTION", "CLIENT"]}>
                    <ClientUpcomingSchedule />
                  </ProtectedRoute>
                </Route>

                {/* Staff routes */}
                <Route path="/staff/dashboard">
                  <ProtectedRoute allowedRoles={["STAFF"]}>
                    <StaffDashboard />
                  </ProtectedRoute>
                </Route>
                <Route path="/staff/schedule">
                  <ProtectedRoute allowedRoles={["STAFF"]}>
                    <StaffSchedule />
                  </ProtectedRoute>
                </Route>
                <Route path="/staff/messages">
                  <ProtectedRoute allowedRoles={["STAFF"]}>
                    <StaffMessages />
                  </ProtectedRoute>
                </Route>

                {/* Client messaging route (in sidebar section) */}
                <Route path="/messages">
                  <ProtectedRoute allowedRoles={["CLIENT"]}>
                    <ClientMessages />
                  </ProtectedRoute>
                </Route>

                <Route component={NotFound} />
              </Switch>
            </main>
            <Footer />
          </div>
        </div>
      </SidebarProvider>
    );
  }

  return (
    <Switch>
      {/* Public routes */}
      <Route path="/login" component={Login} />
      <Route path="/register" component={Register} />
      <Route path="/change-password">
        <ProtectedRoute>
          <ChangePassword />
        </ProtectedRoute>
      </Route>
      <Route path="/reset-password">
        <ProtectedRoute>
          <ResetPassword />
        </ProtectedRoute>
      </Route>

      {/* Client routes */}
      <Route path="/hostesses" component={Hostesses} />
      <Route path="/hostess/:slug" component={HostessProfile} />
      <Route path="/bookings">
        <ProtectedRoute>
          <MyBookings />
        </ProtectedRoute>
      </Route>
      <Route path="/my-bookings">
        <ProtectedRoute>
          <MyBookings />
        </ProtectedRoute>
      </Route>

      {/* Public upcoming schedule - no authentication required */}
      <Route path="/upcoming-schedule" component={ClientUpcomingSchedule} />

      {/* Default route - show homepage for unauthenticated, redirect for authenticated */}
      <Route path="/">
        {isLoading ? (
          <div className="min-h-screen flex items-center justify-center">
            <div className="animate-pulse text-muted-foreground">Loading...</div>
          </div>
        ) : user ? (
          <Redirect to={getDefaultRoute()} />
        ) : (
          <Home />
        )}
      </Route>

      {/* Fallback to 404 */}
      <Route component={NotFound} />
    </Switch>
  );
}

export default function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider defaultTheme="dark">
        <TooltipProvider>
          <AuthProvider>
            <AppRouter />
            <Toaster />
          </AuthProvider>
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

