      icon: Calendar,
    },
    {
      title: "Analytics",
      url: "/admin/analytics",
      icon: BarChart3,
    },
    {
      title: "Users",
      url: "/admin/users",
      icon: UserCog,
    },
    {
      title: "Clients",
      url: "/admin/clients",
      icon: UserCircle,
    },
    {
      title: "Hostesses",
      url: "/admin/hostesses",
      icon: Users,
    },
    {
      title: "Services",
      url: "/admin/services",
      icon: Settings,
    },
    {
      title: "Photo Approvals",
      url: "/admin/photo-approvals",
      icon: ImageIcon,
    },
    {
      title: "Upcoming Schedule",
      url: "/admin/upcoming-schedule",
      icon: CalendarClock,
    },
    {
      title: "Trigger Words",
      url: "/admin/trigger-words",
      icon: Shield,
    },
    {
      title: "Flagged Conversations",
      url: "/admin/flagged-conversations",
      icon: AlertTriangle,
    },
    {
      title: "Import Schedule",
      url: "/admin/import",
      icon: FileUp,
    },
    {
      title: "Export Schedule",
      url: "/admin/export",
      icon: FileDown,
    },
  ];

  const receptionItems = [
    {
      title: "Dashboard",
      url: "/reception/calendar",
      icon: LayoutDashboard,
    },
    {
      title: "Daily Calendar",
      url: "/admin/calendar",
      icon: Calendar,
    },
    {
      title: "Browse Hostesses",
      url: "/hostesses",
      icon: Users,
    },
    {
      title: "Manage Hostesses",
      url: "/admin/hostesses",
      icon: Settings,
    },
    {
      title: "Clients",
      url: "/admin/clients",
      icon: UserCircle,
    },
    {
      title: "Photo Approvals",
      url: "/admin/photo-approvals",
      icon: ImageIcon,
    },
    {
      title: "Upcoming Schedule",
      url: "/admin/upcoming-schedule",
      icon: CalendarClock,
    },
    {
      title: "Import Schedule",
      url: "/admin/import",
      icon: FileUp,
    },
    {
      title: "Export Schedule",
      url: "/admin/export",
      icon: FileDown,
    },
  ];

  const staffItems = [
    {
      title: "Dashboard",
      url: "/staff/dashboard",
      icon: LayoutDashboard,
    },
    {
      title: "My Schedule",
      url: "/staff/schedule",
      icon: Calendar,
    },
    {
      title: "Messages",
      url: "/staff/messages",
      icon: MessageCircle,
    },
  ];

  const clientItems = [
