      title: "My Bookings",
      url: "/bookings",
      icon: Calendar,
    },
    {
      title: "Messages",
      url: "/messages",
      icon: MessageCircle,
    },
    {
      title: "Upcoming Schedule",
      url: "/upcoming-schedule",
      icon: CalendarClock,
    },
  ];

  const getMenuItems = () => {
    if (!user) return clientItems;
    switch (user.role) {
      case "ADMIN":
        return adminItems;
      case "RECEPTION":
        return receptionItems;
      case "STAFF":
        return staffItems;
      default:
        return clientItems;
    }
  };

  const menuItems = getMenuItems();

  return (
    <Sidebar>
