import postgres from "postgres";
import { drizzle } from "drizzle-orm/postgres-js";
import * as schema from "@shared/schema";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL environment variable is required");
}

// Use standard PostgreSQL driver for Replit/Supabase
const queryClient = postgres(process.env.DATABASE_URL, {
  ssl: process.env.NODE_ENV === 'production' ? 'require' : 'prefer',
  max: 10,
});

export const db = drizzle(queryClient, { schema });

client/src/components/quick-booking-form.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { minutesToTime } from "@/lib/time-utils";
import { ChevronDown, Calendar, BanIcon } from "lucide-react";
import type { User, Service } from "@shared/schema";

const quickBookingSchema = z.object({
  clientEmail: z.string().email("Invalid email"),
  serviceId: z.string().min(1, "Please select a service"),
  notes: z.string().optional(),
});

const timeOffSchema = z.object({
  reason: z.string().min(1, "Please provide a reason"),
  duration: z.number().min(15, "Minimum 15 minutes"),
});

type QuickBookingFormData = z.infer<typeof quickBookingSchema>;
type TimeOffFormData = z.infer<typeof timeOffSchema>;

interface QuickBookingFormProps {
  hostessId: string;
  date: string;
  startTime: number;
  onSuccess: () => void;
  onCancel: () => void;
}

export function QuickBookingForm({ hostessId, date, startTime, onSuccess, onCancel }: QuickBookingFormProps) {
  const { toast } = useToast();
  const [mode, setMode] = useState<"booking" | "timeoff">("booking");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedService, setSelectedService] = useState<Service | null>(null);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);

  const { data: allClients } = useQuery<User[]>({
    queryKey: ["/api/clients"],
  });

  const { data: services } = useQuery<Service[]>({
    queryKey: ["/api/services"],
  });

  const sortedServices = services?.slice().sort((a, b) => a.durationMin - b.durationMin) || [];

  const filteredClients = allClients?.filter(client => 
    client.email.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  const createBookingMutation = useMutation({
    mutationFn: async (data: QuickBookingFormData) => {
      if (!selectedService) return;

      const endTime = startTime + selectedService.durationMin;

      return apiRequest("POST", "/api/bookings", {
        hostessId,
        clientEmail: data.clientEmail,
        serviceId: data.serviceId,
        date,
        startTime,
        endTime,
        notes: data.notes || null,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ 
        predicate: (query) => {
          const key = query.queryKey[0];
          return typeof key === 'string' && key.startsWith('/api/bookings');
        }
      });
      toast({
        title: "Booking created",
        description: "Appointment scheduled successfully",
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Booking failed",
        description: error.message || "Could not create booking",
      });
    },
  });

  const createTimeOffMutation = useMutation({
    mutationFn: async (data: TimeOffFormData) => {
      const endTime = startTime + data.duration;

      return apiRequest("POST", "/api/timeoff", {
        hostessId,
        date,
        startTime,
        endTime,
        reason: data.reason,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ 
        predicate: (query) => {
          const key = query.queryKey[0];
          return typeof key === 'string' && (key.startsWith('/api/bookings') || key.startsWith('/api/hostesses'));
        }
      });
      toast({
        title: "Time off created",
        description: "Time slot marked as unavailable",
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Time off failed",
        description: error.message || "Could not create time off",
      });
    },
  });

  const bookingForm = useForm<QuickBookingFormData>({
    resolver: zodResolver(quickBookingSchema),
    defaultValues: {
      clientEmail: "",
      serviceId: "",
      notes: "",
    },
  });

  const timeOffForm = useForm<TimeOffFormData>({
    resolver: zodResolver(timeOffSchema),
    defaultValues: {
      reason: "",
      duration: 60,
    },
  });

  const onBookingSubmit = (data: QuickBookingFormData) => {
    createBookingMutation.mutate(data);
  };

  const onTimeOffSubmit = (data: TimeOffFormData) => {
    createTimeOffMutation.mutate(data);
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (inputRef.current && !inputRef.current.contains(event.target as Node)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <Tabs value={mode} onValueChange={(v) => setMode(v as "booking" | "timeoff")} className="w-full">
      <TabsList className="grid w-full grid-cols-2 mb-6">
        <TabsTrigger value="booking" data-testid="tab-booking">
          <Calendar className="h-4 w-4 mr-2" />
          New Booking
        </TabsTrigger>
        <TabsTrigger value="timeoff" data-testid="tab-timeoff">
          <BanIcon className="h-4 w-4 mr-2" />
          Time Off
        </TabsTrigger>
      </TabsList>

      <div className="grid grid-cols-2 gap-4 p-4 bg-muted rounded-lg mb-6">
        <div>
          <p className="text-sm text-muted-foreground">Date</p>
          <p className="font-medium">{date}</p>
        </div>
        <div>
          <p className="text-sm text-muted-foreground">Start Time</p>
          <p className="font-medium font-mono">{minutesToTime(startTime)}</p>
        </div>
      </div>

      <TabsContent value="booking" className="mt-0">
        <Form {...bookingForm}>
          <form onSubmit={bookingForm.handleSubmit(onBookingSubmit)} className="space-y-6">
            <FormField
              control={bookingForm.control}
              name="clientEmail"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Client Email</FormLabel>
                  <FormControl>
                    <div className="relative" ref={inputRef}>
                      <div className="relative">
                        <Input
                          type="email"
                          placeholder="Search or enter client email..."
                          data-testid="input-client-email"
                          {...field}
                          onChange={(e) => {
                            field.onChange(e);
                            setSearchQuery(e.target.value);
                            setDropdownOpen(true);
                          }}
                          onFocus={() => setDropdownOpen(true)}
                        />
                        <button
                          type="button"
                          onClick={() => setDropdownOpen(!dropdownOpen)}
                          className="absolute right-2 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                          data-testid="button-toggle-clients"
                        >
                          <ChevronDown className="h-4 w-4" />
                        </button>
                      </div>
                      {dropdownOpen && (
                        <div className="absolute top-full left-0 right-0 mt-1 bg-popover border rounded-md shadow-lg max-h-64 overflow-auto z-50">
                          {filteredClients.length > 0 ? (
                            <>
                              <div className="px-3 py-2 text-xs font-medium text-muted-foreground border-b bg-muted/50">
                                {filteredClients.length} client{filteredClients.length !== 1 ? 's' : ''} found
                              </div>
                              {filteredClients.map((client) => (
                                <button
                                  key={client.id}
                                  type="button"
                                  className="w-full px-3 py-2.5 text-left text-sm hover-elevate border-b last:border-b-0"
                                  onClick={() => {
                                    bookingForm.setValue("clientEmail", client.email, { shouldValidate: true });
                                    setSearchQuery(client.email);
                                    setDropdownOpen(false);
                                  }}
                                  data-testid={`button-client-${client.id}`}
                                >
                                  <div className="font-medium">{client.email}</div>
                                </button>
                              ))}
                            </>
                          ) : (
                            <div className="px-3 py-8 text-center text-sm text-muted-foreground">
                              {searchQuery ? (
                                <>
                                  No clients found matching "{searchQuery}"
                                  <div className="text-xs mt-1">Type a new email to create a booking</div>
                                </>
                              ) : (
                                "No clients yet"
                              )}
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={bookingForm.control}
              name="serviceId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Service</FormLabel>
                  <Select 
                    onValueChange={(value) => {
                      field.onChange(value);
                      const service = sortedServices.find(s => s.id === value);
                      setSelectedService(service || null);
                    }}
                    value={field.value}
                  >
                    <FormControl>
                      <SelectTrigger data-testid="select-service">
                        <SelectValue placeholder="Select a service" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {sortedServices.map((service) => (
                        <SelectItem key={service.id} value={service.id}>
                          {service.name} ({service.durationMin} min) - ${(service.priceCents / 100).toFixed(2)}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={bookingForm.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes (Optional)</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Special requests or notes..."
                      data-testid="input-notes"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={onCancel}
                className="flex-1"
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1"
                disabled={createBookingMutation.isPending}
                data-testid="button-create-booking"
              >
                {createBookingMutation.isPending ? "Creating..." : "Create Booking"}
              </Button>
            </div>
          </form>
        </Form>
      </TabsContent>

      <TabsContent value="timeoff" className="mt-0">
        <Form {...timeOffForm}>
          <form onSubmit={timeOffForm.handleSubmit(onTimeOffSubmit)} className="space-y-6">
            <FormField
              control={timeOffForm.control}
              name="duration"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Duration</FormLabel>
                  <Select 
                    onValueChange={(value) => field.onChange(Number(value))}
                    value={field.value.toString()}
                  >
                    <FormControl>
                      <SelectTrigger data-testid="select-duration">
                        <SelectValue />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="15">15 minutes</SelectItem>
                      <SelectItem value="30">30 minutes</SelectItem>
                      <SelectItem value="45">45 minutes</SelectItem>
                      <SelectItem value="60">1 hour</SelectItem>
                      <SelectItem value="90">1.5 hours</SelectItem>
                      <SelectItem value="120">2 hours</SelectItem>
                      <SelectItem value="180">3 hours</SelectItem>
                      <SelectItem value="240">4 hours</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={timeOffForm.control}
              name="reason"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Reason</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Break, lunch, personal time, etc..."
                      data-testid="input-timeoff-reason"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={onCancel}
                className="flex-1"
                data-testid="button-cancel-timeoff"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="destructive"
                className="flex-1"
                disabled={createTimeOffMutation.isPending}
                data-testid="button-create-timeoff"
              >
                {createTimeOffMutation.isPending ? "Creating..." : "Mark as Time Off"}
              </Button>
            </div>
          </form>
        </Form>
      </TabsContent>
    </Tabs>
  );
}

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 220 25% 15%;

  --border: 220 15% 90%;

  --card: 220 20% 98%;

  --card-foreground: 220 25% 15%;

  --card-border: 220 15% 95%;

  --sidebar: 220 15% 95%;

  --sidebar-foreground: 220 25% 15%;

  --sidebar-border: 220 15% 92%;

  --sidebar-primary: 210 90% 45%;

  --sidebar-primary-foreground: 210 90% 98%;

  --sidebar-accent: 220 12% 88%;

  --sidebar-accent-foreground: 220 25% 20%;

  --sidebar-ring: 210 90% 45%;

  --popover: 220 15% 93%;

  --popover-foreground: 220 25% 15%;

  --popover-border: 220 15% 90%;

  --primary: 210 90% 45%;

  --primary-foreground: 210 90% 98%;

  --secondary: 220 12% 88%;

  --secondary-foreground: 220 25% 20%;

  --muted: 220 10% 90%;

  --muted-foreground: 220 15% 45%;

  --accent: 220 8% 87%;

  --accent-foreground: 220 25% 20%;

  --destructive: 0 75% 45%;

  --destructive-foreground: 0 75% 98%;

  --input: 220 20% 70%;
  --ring: 210 90% 45%;
  --chart-1: 210 85% 35%;
  --chart-2: 280 60% 40%;
  --chart-3: 145 55% 35%;
  --chart-4: 25 75% 40%;
  --chart-5: 180 65% 35%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Roboto Mono', 'SF Mono', Consolas, monospace;
  --radius: .375rem; /* 6px */
  --shadow-2xs: 0px 1px 2px 0px hsl(220 15% 15% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 15% 15% / 0.08);
  --shadow-sm: 0px 2px 4px -1px hsl(220 15% 15% / 0.06), 0px 1px 2px -1px hsl(220 15% 15% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(220 15% 15% / 0.08), 0px 2px 4px -2px hsl(220 15% 15% / 0.05);
  --shadow-md: 0px 6px 12px -2px hsl(220 15% 15% / 0.10), 0px 3px 6px -3px hsl(220 15% 15% / 0.06);
  --shadow-lg: 0px 10px 20px -4px hsl(220 15% 15% / 0.12), 0px 4px 8px -4px hsl(220 15% 15% / 0.07);
  --shadow-xl: 0px 20px 40px -8px hsl(220 15% 15% / 0.15), 0px 8px 16px -6px hsl(220 15% 15% / 0.08);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 15% 15% / 0.18);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 15% 12%;

  --foreground: 220 10% 95%;

  --border: 220 12% 18%;

  --card: 220 14% 16%;

  --card-foreground: 220 10% 95%;

  --card-border: 220 13% 18%;

  --sidebar: 220 13% 20%;

  --sidebar-foreground: 220 10% 95%;

  --sidebar-border: 220 12% 22%;

  --sidebar-primary: 210 85% 55%;

  --sidebar-primary-foreground: 210 90% 98%;

  --sidebar-accent: 220 12% 24%;

  --sidebar-accent-foreground: 220 8% 90%;

  --sidebar-ring: 210 85% 55%;

  --popover: 220 12% 24%;

  --popover-foreground: 220 10% 95%;

  --popover-border: 220 11% 26%;

  --primary: 210 85% 55%;

  --primary-foreground: 210 90% 98%;

  --secondary: 220 12% 28%;

  --secondary-foreground: 220 8% 90%;

  --muted: 220 10% 18%;

  --muted-foreground: 220 8% 70%;

  --accent: 220 12% 22%;

  --accent-foreground: 220 8% 90%;

  --destructive: 0 75% 50%;

  --destructive-foreground: 0 75% 98%;

  --input: 220 12% 35%;
  --ring: 210 85% 55%;
  --chart-1: 145 55% 60%;
  --chart-2: 280 60% 65%;
  --chart-3: 210 85% 65%;
  --chart-4: 25 75% 60%;
  --chart-5: 180 65% 60%;

  --shadow-2xs: 0px 1px 2px 0px hsl(220 25% 5% / 0.40);
  --shadow-xs: 0px 1px 3px 0px hsl(220 25% 5% / 0.50);
  --shadow-sm: 0px 2px 4px -1px hsl(220 25% 5% / 0.45), 0px 1px 2px -1px hsl(220 25% 5% / 0.35);
  --shadow: 0px 4px 6px -1px hsl(220 25% 5% / 0.50), 0px 2px 4px -2px hsl(220 25% 5% / 0.40);
  --shadow-md: 0px 6px 12px -2px hsl(220 25% 5% / 0.55), 0px 3px 6px -3px hsl(220 25% 5% / 0.45);
  --shadow-lg: 0px 10px 20px -4px hsl(220 25% 5% / 0.60), 0px 4px 8px -4px hsl(220 25% 5% / 0.50);
  --shadow-xl: 0px 20px 40px -8px hsl(220 25% 5% / 0.65), 0px 8px 16px -6px hsl(220 25% 5% / 0.55);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 25% 5% / 0.70);

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Marquee Animation */
  @keyframes marquee {
    0% {
      transform: translateX(0);
    }
    100% {
      transform: translateX(-50%);
    }
  }

  .animate-marquee {
    animation: marquee 40s linear infinite;
  }

  .animate-marquee:hover {
    animation-play-state: paused;
  }
}

