import { eq, sql } from "drizzle-orm";
import bcrypt from "bcrypt";
import { format, addDays } from "date-fns";

const SALT_ROUNDS = 10;

async function seed() {
  console.log("ðŸŒ± Starting database seed...\n");

  try {
