server/cleanup.ts.content:
import { db } from "./db";
import { bookings, users } from "@shared/schema";
import { eq, and, lt, inArray, sql } from "drizzle-orm";
import { subWeeks, format } from "date-fns";

/**
 * Deletes client booking history older than 2 weeks
 * This runs automatically to maintain privacy and keep the database clean
 */
export async function cleanupOldClientBookings() {
  try {
    const twoWeeksAgo = subWeeks(new Date(), 2);
    const cutoffDate = format(twoWeeksAgo, "yyyy-MM-dd");

    console.log(`ðŸ§¹ Running booking cleanup for records before ${cutoffDate}...`);

    // Get all CLIENT user IDs
    const clientUsers = await db
      .select({ id: users.id })
      .from(users)
      .where(eq(users.role, "CLIENT"));

    const clientIds = clientUsers.map(u => u.id);

    if (clientIds.length === 0) {
      console.log("   âœ“ No client users found, skipping cleanup");
      return 0;
    }

    // Delete bookings older than 2 weeks for CLIENT users only
    const result = await db
      .delete(bookings)
      .where(
        and(
          lt(bookings.date, cutoffDate),
          inArray(bookings.clientId, clientIds)
        )
      )
      .returning({ id: bookings.id });

    const deletedCount = result.length;

    if (deletedCount > 0) {
      console.log(`   âœ“ Deleted ${deletedCount} old client booking(s)`);
    } else {
      console.log(`   âœ“ No old client bookings to delete`);
    }

    return deletedCount;
  } catch (error) {
    console.error("âŒ Error during booking cleanup:", error);
    throw error;
  }
}

/**
 * Schedule cleanup to run periodically
 * Runs every 24 hours
 */
export function scheduleBookingCleanup() {
  // Run immediately on startup
  cleanupOldClientBookings().catch(console.error);

  // Schedule to run every 24 hours
  const TWENTY_FOUR_HOURS = 24 * 60 * 60 * 1000;
  setInterval(() => {
    cleanupOldClientBookings().catch(console.error);
  }, TWENTY_FOUR_HOURS);

  console.log("ðŸ“… Scheduled automatic booking cleanup (runs every 24 hours)");
}

client/src/pages/admin-clients.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Search, Key, ChevronLeft, ChevronRight } from "lucide-react";
import type { User } from "@shared/schema";

const CLIENTS_PER_PAGE = 100;

export default function AdminClients() {
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [resetPasswordUser, setResetPasswordUser] = useState<User | null>(null);
  const [newPassword, setNewPassword] = useState("");
  const { toast } = useToast();

  const { data: users, isLoading } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  // Filter for only CLIENT role users
  const clients = users?.filter(user => user.role === "CLIENT") || [];

  // Filter clients based on search term
  const filteredClients = clients.filter(client => {
    const search = searchTerm.toLowerCase();
    return (
      client.username.toLowerCase().includes(search) ||
      client.email.toLowerCase().includes(search)
    );
  });

  // Sort by username
  const sortedClients = filteredClients.slice().sort((a, b) => 
    a.username.localeCompare(b.username)
  );

  // Reset to page 1 when search term changes
  const handleSearchChange = (value: string) => {
    setSearchTerm(value);
    setCurrentPage(1);
  };

  // Pagination calculations
  const totalPages = Math.ceil(sortedClients.length / CLIENTS_PER_PAGE);
  const startIndex = (currentPage - 1) * CLIENTS_PER_PAGE;
  const endIndex = startIndex + CLIENTS_PER_PAGE;
  const paginatedClients = sortedClients.slice(startIndex, endIndex);

  const handlePreviousPage = () => {
    setCurrentPage(prev => Math.max(1, prev - 1));
  };

  const handleNextPage = () => {
    setCurrentPage(prev => Math.min(totalPages, prev + 1));
  };

  const resetPasswordMutation = useMutation({
    mutationFn: async ({ id, password }: { id: string; password: string }) => {
      return apiRequest("POST", `/api/admin/users/${id}/reset-password`, { password });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      setResetPasswordUser(null);
      setNewPassword("");
      toast({
        title: "Password reset successfully",
        description: "The client can now use their new password",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Failed to reset password",
        description: error.message || "An error occurred",
      });
    },
  });

  const handleResetPassword = () => {
    if (!resetPasswordUser || !newPassword) return;

    if (newPassword.length < 8) {
      toast({
        variant: "destructive",
        title: "Invalid password",
        description: "Password must be at least 8 characters",
      });
      return;
    }

    resetPasswordMutation.mutate({
      id: resetPasswordUser.id,
      password: newPassword,
    });
  };

  return (
    <div className="min-h-screen bg-background p-8">
      <div className="max-w-7xl mx-auto space-y-6">
        <div>
          <h1 className="text-section-title font-semibold">Clients</h1>
          <p className="text-muted-foreground">View and search all client accounts</p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>All Clients ({sortedClients.length})</CardTitle>
            <div className="relative mt-4">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                type="text"
                placeholder="Search by username or email..."
                value={searchTerm}
                onChange={(e) => handleSearchChange(e.target.value)}
                className="pl-10"
                data-testid="input-search-clients"
              />
            </div>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="text-center py-8 text-muted-foreground">Loading...</div>
            ) : sortedClients.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                {searchTerm ? "No clients found matching your search" : "No clients found"}
              </div>
            ) : (
              <>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Username</TableHead>
                      <TableHead>Email</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Force Reset</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {paginatedClients.map((client) => (
                      <TableRow key={client.id} data-testid={`client-${client.id}`}>
                        <TableCell className="font-medium">{client.username}</TableCell>
                        <TableCell>{client.email}</TableCell>
                        <TableCell>
                          {client.banned ? (
                            <Badge variant="destructive" data-testid={`status-banned-${client.id}`}>
                              Banned
                            </Badge>
                          ) : (
                            <Badge variant="secondary" data-testid={`status-active-${client.id}`}>
                              Active
                            </Badge>
                          )}
                        </TableCell>
                        <TableCell>
                          {client.forcePasswordReset ? (
                            <Badge variant="destructive">Yes</Badge>
                          ) : (
                            <Badge variant="outline">No</Badge>
                          )}
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              setResetPasswordUser(client);
                              setNewPassword("");
                            }}
                            className="gap-1"
                            data-testid={`button-reset-password-${client.id}`}
                          >
                            <Key className="h-3 w-3" />
                            Reset Password
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>

                {/* Pagination Controls */}
                {totalPages > 1 && (
                  <div className="flex items-center justify-between pt-4 border-t mt-4">
                    <div className="text-sm text-muted-foreground">
                      Showing {startIndex + 1}-{Math.min(endIndex, sortedClients.length)} of {sortedClients.length} clients
                    </div>
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={handlePreviousPage}
                        disabled={currentPage === 1}
                        data-testid="button-previous-page"
                      >
                        <ChevronLeft className="h-4 w-4" />
                        Previous
                      </Button>
                      <span className="text-sm text-muted-foreground">
                        Page {currentPage} of {totalPages}
                      </span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={handleNextPage}
                        disabled={currentPage === totalPages}
                        data-testid="button-next-page"
                      >
                        Next
                        <ChevronRight className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                )}
              </>
            )}
          </CardContent>
        </Card>

        {/* Reset Password Dialog */}
        <Dialog open={!!resetPasswordUser} onOpenChange={(open) => !open && (setResetPasswordUser(null), setNewPassword(""))}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Reset Password</DialogTitle>
              <DialogDescription>
                Set a new password for {resetPasswordUser?.email}
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4 py-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">New Password</label>
                <Input
                  type="password"
                  placeholder="Enter new password (min 8 characters)"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  onKeyDown={(e) => e.key === "Enter" && handleResetPassword()}
                  data-testid="input-new-password"
                />
              </div>
            </div>
            <DialogFooter>
              <Button
                variant="outline"
                onClick={() => (setResetPasswordUser(null), setNewPassword(""))}
                data-testid="button-cancel-reset"
              >
                Cancel
              </Button>
              <Button
                onClick={handleResetPassword}
                disabled={resetPasswordMutation.isPending || newPassword.length < 8}
                data-testid="button-confirm-reset-password"
              >
                {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

scripts/setup-hostess-accounts.ts.content:
import { db } from "../server/db";
import { users, hostesses } from "@shared/schema";
import { eq, isNull } from "drizzle-orm";
import bcrypt from "bcrypt";

/**
 * Creates STAFF user accounts for all hostesses who don't have one yet
 * Username: First name only (e.g., "Amelia")
 * Initial password: Same as username (e.g., "Amelia")
 * Requires password reset on first login
 */
async function setupHostessAccounts() {
  console.log("ðŸ” Finding hostesses without user accounts...\n");

  // Find all hostesses without linked user accounts
  const hostessesWithoutAccounts = await db
    .select()
    .from(hostesses)
    .where(isNull(hostesses.userId));

  console.log(`Found ${hostessesWithoutAccounts.length} hostesses without accounts:\n`);

  for (const hostess of hostessesWithoutAccounts) {
    // Extract first name from display name (e.g., "Amelia" from "Amelia")
    const firstName = hostess.displayName.split(' ')[0];
    const username = firstName; // Use first name as-is, preserving capitalization
    const email = `${username.toLowerCase()}@clubalpha.ca`;

    // Hash the username as the initial password
    const passwordHash = await bcrypt.hash(firstName, 10);

    try {
      console.log(`Creating account for ${hostess.displayName}:`);
      console.log(`  Username: ${username}`);
      console.log(`  Email: ${email}`);
      console.log(`  Initial password: ${firstName} (must be changed on first login)`);

      // Create the user account
      const [newUser] = await db
        .insert(users)
        .values({
          username,
          email,
          passwordHash,
          role: 'STAFF',
          forcePasswordReset: true, // Require password change on first login
        })
        .returning();

      // Link the user to the hostess
      await db
        .update(hostesses)
        .set({ userId: newUser.id })
        .where(eq(hostesses.id, hostess.id));

      console.log(`  âœ… Account created successfully\n`);
    } catch (error: any) {
      if (error.code === '23505') { // Unique constraint violation
        console.log(`  âš ï¸  Username "${username}" already exists, skipping...\n`);
      } else {
        console.error(`  âŒ Error creating account:`, error.message, '\n');
      }
    }
  }

  console.log("âœ… Setup complete!");
  console.log("\nðŸ“‹ Summary:");
  console.log("All hostesses can now log in with:");
  console.log("  Username: Their first name (e.g., 'Amelia')");
  console.log("  Initial Password: Same as their first name");
  console.log("  They will be required to set a new password on first login\n");

  process.exit(0);
}

setupHostessAccounts().catch((error) => {
  console.error("Fatal error:", error);
  process.exit(1);
});

client/src/pages/admin-hostess-import.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { FileUp, AlertCircle, CheckCircle2, Download } from "lucide-react";

export default function AdminHostessImport() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [csvData, setCsvData] = useState("");
  const [results, setResults] = useState<any>(null);

  const importMutation = useMutation({
    mutationFn: async (data: string) => {
      const response = await apiRequest("POST", "/api/hostesses/import", { csvData: data });
      return response.json();
    },
    onSuccess: (data: any) => {
      setResults(data);
      const successCount = data.results.filter((r: any) => r.success).length;
      const failCount = data.results.filter((r: any) => !r.success).length;

      toast({
        title: "Import completed",
        description: `${successCount} succeeded, ${failCount} failed`,
      });

      // Invalidate hostesses cache to refresh the list
      queryClient.invalidateQueries({ queryKey: ["/api/hostesses"] });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Import failed",
        description: error.message,
      });
    },
  });

  const handleImport = () => {
    if (!csvData.trim()) {
      toast({
        variant: "destructive",
        title: "No CSV data",
        description: "Please paste CSV data to import",
      });
      return;
    }
    setResults(null);
    importMutation.mutate(csvData);
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target?.result as string;
        setCsvData(text);
      };
      reader.readAsText(file);
    }
  };

  const downloadTemplate = () => {
    const template = `display_name,bio,specialties,location,active
Sophia Martinez,Skilled in deep tissue and relaxation,"Swedish,Deep Tissue,Hot Stone",DOWNTOWN,true
Isabella Chen,Specializes in holistic wellness,"Thai Massage,Shiatsu,Energy Work",DOWNTOWN,true
Olivia Thompson,Expert in therapeutic massage,"Sports Massage,Trigger Point,Myofascial",WEST_END,true`;

    const blob = new Blob([template], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'hostess-import-template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">Import Hostesses</h1>
        <p className="text-muted-foreground">
          Upload or paste CSV data to bulk create or update hostess profiles
        </p>
      </div>

      <div className="grid gap-6">
        <Card>
          <CardHeader>
            <CardTitle>CSV Format</CardTitle>
            <CardDescription>
              Upload a CSV file or paste CSV data with the following format
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Alert className="mb-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                <strong>Expected format:</strong> display_name,bio,specialties,location,active
                <br />
                <strong>Required fields:</strong> display_name, location
                <br />
                <strong>Location values:</strong> DOWNTOWN or WEST_END
                <br />
                <strong>Specialties:</strong> Comma-separated list in quotes (e.g., "Swedish,Deep Tissue,Hot Stone")
                <br />
                <strong>Active:</strong> true or false (default: true)
                <br />
                <strong>Note:</strong> If a hostess with the same name and location exists, their profile will be updated
              </AlertDescription>
            </Alert>

            <div className="space-y-4">
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  onClick={downloadTemplate}
                  className="gap-2"
                  data-testid="button-download-template"
                >
                  <Download className="h-4 w-4" />
                  Download Template
                </Button>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Upload CSV File
                </label>
                <input
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  className="block w-full text-sm text-muted-foreground
                    file:mr-4 file:py-2 file:px-4
                    file:rounded-md file:border-0
                    file:text-sm file:font-semibold
                    file:bg-primary file:text-primary-foreground
                    hover:file:bg-primary/90"
                  data-testid="input-file-upload"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Or Paste CSV Data
                </label>
                <Textarea
                  placeholder="Paste CSV data here..."
                  value={csvData}
                  onChange={(e) => setCsvData(e.target.value)}
                  rows={10}
                  className="font-mono text-sm"
                  data-testid="textarea-csv-data"
                />
              </div>

              <Button
                onClick={handleImport}
                disabled={importMutation.isPending || !csvData.trim()}
                className="w-full"
                data-testid="button-import"
              >
                <FileUp className="h-4 w-4 mr-2" />
                {importMutation.isPending ? "Importing..." : "Import Hostesses"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {results && (
          <Card>
            <CardHeader>
              <CardTitle>Import Results</CardTitle>
              <CardDescription>
                {results.total} rows processed
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-2 max-h-96 overflow-y-auto">
                {results.results.map((result: any, index: number) => (
                  <div
                    key={index}
                    className={`flex items-start gap-2 p-3 rounded-md ${
                      result.success ? "bg-green-500/10" : "bg-red-500/10"
                    }`}
                  >
                    {result.success ? (
                      <CheckCircle2 className="h-5 w-5 text-green-600 dark:text-green-400 flex-shrink-0 mt-0.5" />
                    ) : (
                      <AlertCircle className="h-5 w-5 text-red-600 dark:text-red-400 flex-shrink-0 mt-0.5" />
                    )}
                    <div className="flex-1 min-w-0">
                      <p className="font-medium">
                        {result.success ? (
                          <>
                            {result.action === 'created' ? 'Created' : 'Updated'}: {result.hostess}
                          </>
                        ) : (
                          'Failed'
                        )}
                      </p>
                      {!result.success && result.error && (
                        <p className="text-sm text-muted-foreground">{result.error}</p>
                      )}
                      {result.row && (
                        <p className="text-xs text-muted-foreground mt-1 font-mono truncate">
                          {JSON.stringify(result.row)}
                        </p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin-client-import.tsx.content:
import { useState } from "react";
import { useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { FileUp, AlertCircle, CheckCircle2, Download, Info } from "lucide-react";

export default function AdminClientImport() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [csvData, setCsvData] = useState("");
  const [results, setResults] = useState<any>(null);
  const [isImporting, setIsImporting] = useState(false);
  const [progress, setProgress] = useState({ current: 0, total: 0, currentEmail: "" });

  const startImport = async (data: string) => {
    setIsImporting(true);
    setResults(null);
    setProgress({ current: 0, total: 0, currentEmail: "" });

    try {
      const token = localStorage.getItem("auth_token");
      const response = await fetch("/api/clients/bulk-import-stream", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          ...(token && { "Authorization": `Bearer ${token}` }),
        },
        body: JSON.stringify({ csvData: data }),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`Failed to start import: ${response.statusText}`);
      }

      const reader = response.body?.getReader();
      const decoder = new TextDecoder();

      if (!reader) {
        throw new Error("Failed to read response stream");
      }

      let buffer = "";
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split("\n");
        buffer = lines.pop() || "";

        for (const line of lines) {
          if (line.trim()) {
            const data = JSON.parse(line);

            if (data.type === "total") {
              setProgress(prev => ({ ...prev, total: data.count }));
            } else if (data.type === "progress") {
              setProgress(prev => ({
                current: data.index,
                total: prev.total,
                currentEmail: data.email
              }));
            } else if (data.type === "complete") {
              setResults(data);
              toast({
                title: "Import completed",
                description: `${data.imported} clients imported, ${data.failed} failed`,
              });
              queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
            } else if (data.type === "error") {
              throw new Error(data.message);
            }
          }
        }
      }
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Import failed",
        description: error.message,
      });
    } finally {
      setIsImporting(false);
    }
  };

  const handleImport = () => {
    if (!csvData.trim()) {
      toast({
        variant: "destructive",
        title: "No CSV data",
        description: "Please paste CSV data to import",
      });
      return;
    }

    const lineCount = csvData.trim().split('\n').length - 1; // Subtract header
    if (lineCount > 20000) {
      toast({
        variant: "destructive",
        title: "Too many records",
        description: "Please limit imports to 20,000 records at a time",
      });
      return;
    }

    startImport(csvData);
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target?.result as string;
        setCsvData(text);

        const lineCount = text.trim().split('\n').length - 1;
        toast({
          title: "File loaded",
          description: `${lineCount.toLocaleString()} clients ready to import`,
        });
      };
      reader.readAsText(file);
    }
  };

  const downloadTemplate = () => {
    const template = `email
john.doe@example.com
jane.smith@clubalpha.ca
michael.johnson@gmail.com
sarah.williams@yahoo.com
david.brown@outlook.com
emma.davis@hotmail.com
james.wilson@example.org
olivia.martinez@example.net
robert.anderson@example.co
sophia.taylor@example.io`;

    const blob = new Blob([template], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'client-import-template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadFailedRows = () => {
    if (!results || !results.results) return;

    const failedRows = results.results.filter((r: any) => !r.success);
    const csvContent = 'email,error\n' + 
      failedRows.map((r: any) => `${r.email || r.row?.email || 'unknown'},"${r.error}"`).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'failed-imports.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">Import Clients</h1>
        <p className="text-muted-foreground">
          Bulk upload client email addresses - optimized for large datasets (up to 20,000 records)
        </p>
      </div>

      <div className="grid gap-6">
        <Card>
          <CardHeader>
            <CardTitle>How to Import Clients</CardTitle>
            <CardDescription>
              Follow these steps to successfully import your client list
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3">
              <div>
                <h4 className="font-semibold text-sm mb-2">Step 1: Prepare Your CSV File</h4>
                <p className="text-sm text-muted-foreground">
                  Your CSV file should have just one column with the header "email". Download the template below to get started.
                </p>
              </div>

              <div>
                <h4 className="font-semibold text-sm mb-2">Step 2: Upload or Paste</h4>
                <p className="text-sm text-muted-foreground">
                  Either upload your CSV file using the file picker below, or paste the CSV data directly into the text area.
                </p>
              </div>

              <div>
                <h4 className="font-semibold text-sm mb-2">Step 3: Review Count & Import</h4>
                <p className="text-sm text-muted-foreground">
                  The system will show you how many clients are ready to import. Click "Import Clients" to start the process.
                </p>
              </div>
            </div>

            <Alert>
              <Info className="h-4 w-4" />
              <AlertDescription>
                <strong>What Happens During Import:</strong>
                <br />
                â€¢ <strong>Usernames:</strong> Automatically extracted from emails (part before @)
                <br />
                â€¢ <strong>Example:</strong> john.smith@example.com â†’ username: "john.smith"
                <br />
                â€¢ <strong>Default Password:</strong> Set to their username (e.g., john.smith logs in with "john.smith" as password)
                <br />
                â€¢ <strong>Password Change Required:</strong> All imported clients must change their password on first login
                <br />
                â€¢ <strong>Duplicates:</strong> Existing emails are automatically skipped (no duplicates created)
              </AlertDescription>
            </Alert>

            <Alert className="border-green-500/50 bg-green-500/10">
              <CheckCircle2 className="h-4 w-4 text-green-600 dark:text-green-400" />
              <AlertDescription className="text-green-800 dark:text-green-200">
                <strong>Performance & Capacity:</strong>
                <br />
                â€¢ <strong>Speed:</strong> ~1,000 clients per minute (~2-3 min for 14,000 clients)
                <br />
                â€¢ <strong>Batch Processing:</strong> Clients processed in groups of 100 for optimal performance
                <br />
                â€¢ <strong>Maximum:</strong> Up to 20,000 clients per import
                <br />
                â€¢ <strong>After Import:</strong> Clients appear immediately in the clients list (cache auto-refreshes)
              </AlertDescription>
            </Alert>

            <Alert className="border-amber-500/50 bg-amber-500/10">
              <AlertCircle className="h-4 w-4 text-amber-600 dark:text-amber-400" />
              <AlertDescription className="text-amber-800 dark:text-amber-200">
                <strong>Important for Large Imports (14,000+ clients):</strong>
                <br />
                â€¢ Keep this browser tab open during the entire import
                <br />
                â€¢ Don't navigate away or refresh the page until complete
                <br />
                â€¢ Wait for the "Import completed" message before proceeding
                <br />
                â€¢ If any clients fail, you can download the failed rows and try again
              </AlertDescription>
            </Alert>

            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={downloadTemplate}
                className="gap-2"
                data-testid="button-download-template"
              >
                <Download className="h-4 w-4" />
                Download Template
              </Button>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Upload Client List</CardTitle>
            <CardDescription>
              Upload CSV file or paste email list below
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">
                Upload CSV File
              </label>
              <input
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                disabled={isImporting}
                className="block w-full text-sm text-muted-foreground
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-primary file:text-primary-foreground
                  hover:file:bg-primary/90
                  disabled:opacity-50 disabled:cursor-not-allowed"
                data-testid="input-file-upload"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">
                Or Paste CSV Data
              </label>
              <Textarea
                placeholder="email&#10;client1@example.com&#10;client2@example.com&#10;..."
                value={csvData}
                onChange={(e) => setCsvData(e.target.value)}
                disabled={isImporting}
                rows={10}
                className="font-mono text-sm"
                data-testid="textarea-csv-data"
              />
              {csvData && (
                <p className="text-sm text-muted-foreground mt-2">
                  {(csvData.trim().split('\n').length - 1).toLocaleString()} clients ready to import
                </p>
              )}
            </div>

            <Button
              onClick={handleImport}
              disabled={isImporting || !csvData.trim()}
              className="w-full"
              size="lg"
              data-testid="button-import"
            >
              <FileUp className="h-4 w-4 mr-2" />
              {isImporting ? "Importing..." : "Import Clients"}
            </Button>

            {isImporting && (
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">
                    Importing {progress.current.toLocaleString()} of {progress.total.toLocaleString()}...
                  </span>
                  <span className="font-medium">{progress.total > 0 ? Math.round((progress.current / progress.total) * 100) : 0}%</span>
                </div>
                <Progress value={progress.total > 0 ? (progress.current / progress.total) * 100 : 0} className="w-full" />
                <p className="text-xs text-muted-foreground text-center truncate">
                  Current: {progress.currentEmail || "Preparing..."}
                </p>
              </div>
            )}
          </CardContent>
        </Card>

        {results && (
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Import Results</CardTitle>
                  <CardDescription>
                    {results.total.toLocaleString()} rows processed
                  </CardDescription>
                </div>
                {results.failed > 0 && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={downloadFailedRows}
                    className="gap-2"
                    data-testid="button-download-failed"
                  >
                    <Download className="h-4 w-4" />
                    Download Failed Rows
                  </Button>
                )}
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div className="bg-green-500/10 p-4 rounded-md border border-green-500/20">
                  <div className="text-2xl font-bold text-green-600 dark:text-green-400">
                    {results.imported.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">Successfully imported</div>
                </div>
                <div className="bg-red-500/10 p-4 rounded-md border border-red-500/20">
                  <div className="text-2xl font-bold text-red-600 dark:text-red-400">
                    {results.failed.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">Failed</div>
                </div>
              </div>

              {results.failed > 0 && (
                <div className="space-y-2 max-h-96 overflow-y-auto">
                  <h4 className="font-medium text-sm mb-2">Failed Imports:</h4>
                  {results.results
                    .filter((r: any) => !r.success)
                    .slice(0, 100) // Show first 100 failures
                    .map((result: any, index: number) => (
                      <div
                        key={index}
                        className="flex items-start gap-2 p-3 rounded-md bg-red-500/10"
                      >
                        <AlertCircle className="h-5 w-5 text-red-600 dark:text-red-400 flex-shrink-0 mt-0.5" />
                        <div className="flex-1 min-w-0">
                          <p className="font-medium">{result.email || result.row?.email || 'Unknown'}</p>
                          <p className="text-sm text-muted-foreground">{result.error}</p>
                        </div>
                      </div>
                    ))}
                  {results.failed > 100 && (
                    <p className="text-sm text-muted-foreground text-center mt-4">
                      Showing first 100 failures. Download the full list using the button above.
                    </p>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/staff-dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Calendar, Clock, CalendarX, User } from "lucide-react";
import { formatTimeRange } from "@/lib/time-utils";
import type { BookingWithDetails, Hostess, TimeOff, WeeklySchedule } from "@shared/schema";
import { useAuth } from "@/lib/auth-context";

interface StaffOverview {
  hostess: Hostess | null;
  todayBookings: BookingWithDetails[];
  tomorrowBookings: BookingWithDetails[];
  todayTimeOff: TimeOff[];
  weeklySchedule: WeeklySchedule[];
  upcomingBookings: BookingWithDetails[];
}

export default function StaffDashboard() {
  const { user } = useAuth();

  // Get all staff data in one optimized API call
  const { data: overview, isLoading } = useQuery<StaffOverview>({
    queryKey: ["/api/staff/overview"],
  });

  const linkedHostess = overview?.hostess;
  const todayBookings = overview?.todayBookings || [];
  const tomorrowBookings = overview?.tomorrowBookings || [];
  const todayTimeOff = overview?.todayTimeOff || [];
  const weeklySchedule = overview?.weeklySchedule || [];
  const upcomingBookings = overview?.upcomingBookings || [];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background p-4 md:p-6">
        <div className="max-w-6xl mx-auto">
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground">Loading your dashboard...</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!linkedHostess) {
    return (
      <div className="min-h-screen bg-background p-4 md:p-6">
        <div className="max-w-6xl mx-auto">
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground">
                Your account is not yet linked to a hostess profile. Please contact an administrator.
              </p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const firstName = linkedHostess.displayName.split(' ')[0];
  const workingDays = weeklySchedule.filter(s => s.startTime && s.endTime).map(s => s.weekday);
  const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div className="min-h-screen bg-background p-4 md:p-6">
      <div className="max-w-6xl mx-auto space-y-4">
        {/* Welcome Header */}
        <div>
          <h1 className="text-2xl font-bold mb-1">Welcome back, {firstName}!</h1>
          <p className="text-sm text-muted-foreground">Your personal Club Alpha staff dashboard</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Today's Appointments */}
          <Card className="bg-gradient-to-br from-purple-500 to-purple-600 border-0 text-white">
            <CardContent className="p-4 text-center">
              <Calendar className="h-6 w-6 mx-auto mb-2 opacity-80" />
              <div className="text-2xl font-bold mb-1">{todayBookings.length}</div>
              <div className="text-sm font-medium">Your Appointments Today</div>
            </CardContent>
          </Card>

          {/* Tomorrow's Bookings */}
          <Card className="bg-gradient-to-br from-pink-500 to-pink-600 border-0 text-white">
            <CardContent className="p-4 text-center">
              <Clock className="h-6 w-6 mx-auto mb-2 opacity-80" />
              <div className="text-2xl font-bold mb-1">{tomorrowBookings.length}</div>
              <div className="text-sm font-medium">Tomorrow's Bookings</div>
            </CardContent>
          </Card>

          {/* Today's Time Off */}
          <Card className="bg-gradient-to-br from-blue-500 to-blue-600 border-0 text-white">
            <CardContent className="p-4 text-center">
              <CalendarX className="h-6 w-6 mx-auto mb-2 opacity-80" />
              <div className="text-2xl font-bold mb-1">{todayTimeOff.length}</div>
              <div className="text-sm font-medium">Today's Time Off</div>
            </CardContent>
          </Card>
        </div>

        {/* Profile and Today's Schedule Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
          {/* Your Profile */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5 text-purple-500" />
                Your Profile
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="flex items-center gap-4">
                <Avatar className="h-20 w-20">
                  <AvatarImage src={linkedHostess.photoUrl || undefined} />
                  <AvatarFallback className="text-2xl bg-purple-500 text-white">
                    {linkedHostess.displayName.split(' ').map((n: string) => n[0]).join('')}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h3 className="text-xl font-bold">{linkedHostess.displayName}</h3>
                  <p className="text-muted-foreground">
                    {linkedHostess.location === "DOWNTOWN" ? "Downtown" : "West End"}
                  </p>
                </div>
              </div>

              <div>
                <h4 className="font-semibold mb-3">Working Days:</h4>
                <div className="flex gap-2 flex-wrap">
                  {dayNames.map((day, index) => {
                    const isWorkingDay = workingDays.includes(index);
                    return (
                      <Badge
                        key={day}
                        variant={isWorkingDay ? "default" : "outline"}
                        className={isWorkingDay ? "bg-purple-500" : ""}
                        data-testid={`badge-${day.toLowerCase()}`}
                      >
                        {day}
                      </Badge>
                    );
                  })}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Your Schedule Today */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5 text-purple-500" />
                Your Schedule Today
              </CardTitle>
            </CardHeader>
            <CardContent>
              {todayBookings.length === 0 ? (
                <div className="py-6 text-center text-sm text-muted-foreground">
                  No appointments scheduled for today
                </div>
              ) : (
                <div className="space-y-2">
                  {todayBookings.map((booking: BookingWithDetails) => (
                    <div
                      key={booking.id}
                      className="p-3 rounded-md border bg-card hover-elevate"
                      data-testid={`booking-${booking.id}`}
                    >
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium truncate">{booking.client?.email || "Client"}</p>
                          <p className="text-xs text-muted-foreground">{booking.service?.name}</p>
                        </div>
                        <Badge variant="outline" className="flex-shrink-0 text-xs">
                          {formatTimeRange(booking.startTime, booking.endTime)}
                        </Badge>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Upcoming Appointments */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Clock className="h-5 w-5 text-purple-500" />
              Your Upcoming Appointments
            </CardTitle>
            <p className="text-sm text-muted-foreground mt-1">All your scheduled appointments</p>
          </CardHeader>
          <CardContent>
            {upcomingBookings.length === 0 ? (
              <div className="py-6 text-center text-sm text-muted-foreground">
                No upcoming appointments
              </div>
            ) : (
              <div className="space-y-2">
                {upcomingBookings.map((booking: BookingWithDetails) => (
                  <div
                    key={booking.id}
                    className="p-3 rounded-md border bg-card hover-elevate"
                    data-testid={`upcoming-booking-${booking.id}`}
                  >
                    <div className="flex items-center justify-between gap-3">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <Badge variant="outline" className="text-xs">{booking.date}</Badge>
                          <Badge variant="outline" className="text-xs">{formatTimeRange(booking.startTime, booking.endTime)}</Badge>
                        </div>
                        <p className="text-sm font-medium">{booking.client?.email || "Client"}</p>
                        <p className="text-xs text-muted-foreground">{booking.service?.name}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin-upcoming-schedule.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { FileUp, AlertCircle, CheckCircle2, Download, Info, Trash2, Calendar } from "lucide-react";

export default function AdminUpcomingSchedule() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [csvData, setCsvData] = useState("");
  const [results, setResults] = useState<any>(null);

  const uploadMutation = useMutation({
    mutationFn: async (data: string) => {
      const response = await apiRequest("POST", "/api/upcoming-schedule/bulk", { csvData: data });
      return response.json();
    },
    onSuccess: (data: any) => {
      setResults(data);

      const successCount = data.results.filter((r: any) => r.success).length;
      const failedCount = data.results.filter((r: any) => !r.success).length;

      toast({
        title: "Upload completed",
        description: `${successCount} schedule slots uploaded, ${failedCount} failed`,
      });

      // Invalidate schedule cache
      queryClient.invalidateQueries({ queryKey: ["/api/upcoming-schedule"] });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Upload failed",
        description: error.message,
      });
    },
  });

  const clearMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("DELETE", "/api/upcoming-schedule/clear");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Schedule cleared",
        description: "All upcoming schedule data has been deleted",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/upcoming-schedule"] });
      setResults(null);
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Clear failed",
        description: error.message,
      });
    },
  });

  const handleUpload = () => {
    if (!csvData.trim()) {
      toast({
        variant: "destructive",
        title: "No CSV data",
        description: "Please paste CSV data to upload",
      });
      return;
    }

    setResults(null);
    uploadMutation.mutate(csvData);
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target?.result as string;
        setCsvData(text);

        const lineCount = text.trim().split('\n').length - 1;
        toast({
          title: "File loaded",
          description: `${lineCount.toLocaleString()} schedule slots ready to upload`,
        });
      };
      reader.readAsText(file);
    }
  };

  const downloadTemplate = () => {
    const template = `date,hostess,startTime,endTime,service,notes
2025-10-29,Sophia,10:00,11:00,Premium Experience,
2025-10-29,Amelia,14:00,15:30,VIP Session,
2025-10-29,Olivia,18:00,19:00,Standard Session,
2025-10-30,Isabella,10:30,12:00,Premium Experience,Preview slot
2025-10-30,Charlotte,15:00,16:30,VIP Session,`;

    const blob = new Blob([template], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'upcoming-schedule-template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadFailedRows = () => {
    if (!results || !results.results) return;

    const failedRows = results.results.filter((r: any) => !r.success);
    const csvContent = 'date,hostess,startTime,endTime,service,notes,error\n' + 
      failedRows.map((r: any) => {
        const row = r.row || {};
        return `${row.date || ''},${row.hostess || ''},${row.startTime || ''},${row.endTime || ''},${row.service || ''},${row.notes || ''},"${r.error}"`;
      }).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'failed-uploads.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2" data-testid="text-page-title">Upcoming Schedule Upload</h1>
        <p className="text-muted-foreground" data-testid="text-page-description">
          Upload the preview schedule for the upcoming week. Clients can view but cannot book through the app.
        </p>
      </div>

      <div className="grid gap-6">
        {/* Instructions Card */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Info className="h-5 w-5" />
              How to Upload Schedule
            </CardTitle>
            <CardDescription>
              Follow these steps to upload the upcoming schedule for client preview
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <h3 className="font-semibold">CSV Format:</h3>
              <p className="text-sm text-muted-foreground">
                Your CSV must have the following columns: <code className="bg-muted px-1 rounded">date,hostess,startTime,endTime,service,notes</code>
              </p>
              <ul className="list-disc list-inside text-sm text-muted-foreground space-y-1 ml-2">
                <li><strong>date:</strong> Format YYYY-MM-DD (e.g., 2025-10-29)</li>
                <li><strong>hostess:</strong> Hostess display name (e.g., Sophia, Amelia)</li>
                <li><strong>startTime:</strong> Format HH:MM in 24-hour time (e.g., 14:00)</li>
                <li><strong>endTime:</strong> Format HH:MM in 24-hour time (e.g., 15:30)</li>
                <li><strong>service:</strong> Service name (optional, e.g., Premium Experience)</li>
                <li><strong>notes:</strong> Optional notes (e.g., Preview slot)</li>
              </ul>
            </div>

            <div className="flex gap-2">
              <Button 
                variant="outline" 
                onClick={downloadTemplate}
                data-testid="button-download-template"
              >
                <Download className="h-4 w-4 mr-2" />
                Download Template
              </Button>

              <label>
                <input
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  className="hidden"
                  data-testid="input-file-upload"
                />
                <Button variant="outline" asChild>
                  <span>
                    <FileUp className="h-4 w-4 mr-2" />
                    Choose CSV File
                  </span>
                </Button>
              </label>
            </div>
          </CardContent>
        </Card>

        {/* Upload Card */}
        <Card>
          <CardHeader>
            <CardTitle>Upload CSV Data</CardTitle>
            <CardDescription>
              Paste your CSV data or use the file upload button above
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <Textarea
              placeholder="Paste CSV data here..."
              value={csvData}
              onChange={(e) => setCsvData(e.target.value)}
              className="min-h-[200px] font-mono text-sm"
              data-testid="input-csv-data"
            />

            {uploadMutation.isPending && (
              <div className="space-y-2">
                <Progress value={undefined} className="w-full" data-testid="progress-upload" />
                <p className="text-sm text-muted-foreground text-center">
                  Uploading schedule...
                </p>
              </div>
            )}

            <div className="flex gap-2">
              <Button 
                onClick={handleUpload} 
                disabled={!csvData.trim() || uploadMutation.isPending}
                data-testid="button-upload"
              >
                <FileUp className="h-4 w-4 mr-2" />
                Upload Schedule
              </Button>

              <Button
                variant="destructive"
                onClick={() => {
                  if (confirm("Are you sure you want to clear all upcoming schedule data? This cannot be undone.")) {
                    clearMutation.mutate();
                  }
                }}
                disabled={clearMutation.isPending}
                data-testid="button-clear-all"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Clear All Schedule
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Results Card */}
        {results && (
          <Card>
            <CardHeader>
              <CardTitle>Upload Results</CardTitle>
              <CardDescription>
                Summary of uploaded schedule slots
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="flex items-center gap-2">
                  <CheckCircle2 className="h-5 w-5 text-green-500" />
                  <div>
                    <p className="text-sm text-muted-foreground">Successful</p>
                    <p className="text-2xl font-bold" data-testid="text-success-count">
                      {results.results.filter((r: any) => r.success).length}
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <AlertCircle className="h-5 w-5 text-destructive" />
                  <div>
                    <p className="text-sm text-muted-foreground">Failed</p>
                    <p className="text-2xl font-bold" data-testid="text-failed-count">
                      {results.results.filter((r: any) => !r.success).length}
                    </p>
                  </div>
                </div>
              </div>

              {results.results.some((r: any) => !r.success) && (
                <>
                  <Alert variant="destructive">
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>
                      Some schedule slots failed to upload. Download the error report to see details.
                    </AlertDescription>
                  </Alert>

                  <Button 
                    variant="outline" 
                    onClick={downloadFailedRows}
                    data-testid="button-download-failed"
                  >
                    <Download className="h-4 w-4 mr-2" />
                    Download Failed Rows
                  </Button>

                  <div className="max-h-60 overflow-auto border rounded-md">
                    <table className="w-full text-sm">
                      <thead className="bg-muted sticky top-0">
                        <tr>
                          <th className="text-left p-2">Date</th>
                          <th className="text-left p-2">Hostess</th>
                          <th className="text-left p-2">Time</th>
                          <th className="text-left p-2">Error</th>
                        </tr>
                      </thead>
                      <tbody>
                        {results.results
                          .filter((r: any) => !r.success)
                          .map((r: any, i: number) => (
                            <tr key={i} className="border-t">
                              <td className="p-2">{r.row?.date || 'N/A'}</td>
                              <td className="p-2">{r.row?.hostess || 'N/A'}</td>
                              <td className="p-2 font-mono">
                                {r.row?.startTime && r.row?.endTime 
                                  ? `${r.row.startTime}-${r.row.endTime}`
                                  : 'N/A'
                                }
                              </td>
                              <td className="p-2 text-destructive">{r.error}</td>
                            </tr>
                          ))}
                      </tbody>
                    </table>
                  </div>
                </>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/client-messages.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { MessageCircle, Send, ArrowLeft } from "lucide-react";
import { format } from "date-fns";
import type { ConversationWithDetails, MessageWithSender, Hostess } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export default function ClientMessages() {
  const { toast } = useToast();
  const [selectedConversation, setSelectedConversation] = useState<ConversationWithDetails | null>(null);
  const [messageInput, setMessageInput] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Fetch conversations with real-time updates every 5 seconds
  const { data: conversations = [], isLoading: isLoadingConversations } = useQuery<ConversationWithDetails[]>({
    queryKey: ["/api/conversations"],
    refetchInterval: 5000, // Auto-refresh every 5 seconds
  });

  // Fetch messages for selected conversation with real-time updates
  const { data: messages = [], isLoading: isLoadingMessages } = useQuery<MessageWithSender[]>({
    queryKey: ["/api/conversations", selectedConversation?.id, "messages"],
    enabled: !!selectedConversation,
    refetchInterval: selectedConversation ? 3000 : false, // Auto-refresh every 3 seconds when viewing
  });

  // Mark conversation as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (conversationId: string) => {
      return apiRequest("POST", `/api/conversations/${conversationId}/read`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
    },
  });

  // Fetch all hostesses to allow starting new conversations
  const { data: hostesses = [] } = useQuery<Hostess[]>({
    queryKey: ["/api/hostesses"],
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      if (!selectedConversation) return;
      return apiRequest("POST", "/api/messages", {
        conversationId: selectedConversation.id,
        content,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations", selectedConversation?.id, "messages"] });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setMessageInput("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Start new conversation mutation
  const startConversationMutation = useMutation({
    mutationFn: async (hostessId: string) => {
      const response = await apiRequest("POST", "/api/conversations", { hostessId });
      return response.json() as Promise<ConversationWithDetails>;
    },
    onSuccess: (data: ConversationWithDetails) => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setSelectedConversation(data);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to start conversation. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Mark conversation as read when selected
  useEffect(() => {
    if (selectedConversation) {
      markAsReadMutation.mutate(selectedConversation.id);
    }
  }, [selectedConversation?.id]);

  const handleSendMessage = () => {
    if (!messageInput.trim()) return;
    sendMessageMutation.mutate(messageInput);
  };

  const handleStartConversation = (hostessId: string) => {
    // Check if conversation already exists
    const existing = conversations.find(c => c.hostessId === hostessId);
    if (existing) {
      setSelectedConversation(existing);
    } else {
      startConversationMutation.mutate(hostessId);
    }
  };

  // Get current user ID from first message sent by user
  const currentUserId = messages.find(m => m.sender)?.sender?.id;

  return (
    <div className="h-full flex flex-col bg-background">
      <div className="border-b p-4">
        <h1 className="text-2xl font-semibold">Messages</h1>
        <p className="text-sm text-muted-foreground mt-1">
          Chat with hostesses directly
        </p>
      </div>

      <div className="flex-1 flex overflow-hidden">
        {/* Conversations List */}
        {!selectedConversation ? (
          <div className="flex-1 p-4 overflow-auto">
            <div className="max-w-3xl mx-auto space-y-4">
              {/* Existing Conversations */}
              {conversations.length > 0 && (
                <div className="space-y-2">
                  <h2 className="text-lg font-semibold mb-3">Your Conversations</h2>
                  {conversations.map((conversation) => (
                    <Card
                      key={conversation.id}
                      className="p-4 cursor-pointer hover-elevate active-elevate-2 transition-colors"
                      onClick={() => setSelectedConversation(conversation)}
                      data-testid={`conversation-${conversation.id}`}
                    >
                      <div className="flex items-center gap-3">
                        <Avatar className="h-12 w-12">
                          <AvatarImage src={conversation.hostess.photoUrl || undefined} />
                          <AvatarFallback>
                            {conversation.hostess.displayName.split(' ').map(n => n[0]).join('')}
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-2">
                              <h3 className="font-semibold truncate">
                                {conversation.hostess.displayName}
                              </h3>
                              {conversation.unreadCount && conversation.unreadCount > 0 && (
                                <Badge variant="default" className="text-xs px-2 py-0.5">
                                  {conversation.unreadCount}
                                </Badge>
                              )}
                            </div>
                            {conversation.lastMessageAt && (
                              <span className="text-xs text-muted-foreground">
                                {format(new Date(conversation.lastMessageAt), "MMM d, h:mm a")}
                              </span>
                            )}
                          </div>
                          {conversation.lastMessage && (
                            <p className="text-sm text-muted-foreground truncate mt-1">
                              {conversation.lastMessage.content}
                            </p>
                          )}
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              )}

              {/* Start New Conversation */}
              <div className="space-y-2">
                <h2 className="text-lg font-semibold mb-3">Start a Conversation</h2>
                <div className="grid gap-3 sm:grid-cols-2">
                  {hostesses
                    .filter(h => !conversations.some(c => c.hostessId === h.id))
                    .map((hostess) => (
                      <Card
                        key={hostess.id}
                        className="p-4 cursor-pointer hover-elevate active-elevate-2"
                        onClick={() => handleStartConversation(hostess.id)}
                        data-testid={`start-conversation-${hostess.id}`}
                      >
                        <div className="flex items-center gap-3">
                          <Avatar className="h-10 w-10">
                            <AvatarImage src={hostess.photoUrl || undefined} />
                            <AvatarFallback className="text-xs">
                              {hostess.displayName.split(' ').map(n => n[0]).join('')}
                            </AvatarFallback>
                          </Avatar>
                          <div className="flex-1 min-w-0">
                            <h3 className="font-medium truncate">{hostess.displayName}</h3>
                            {hostess.locations && hostess.locations.length > 0 && (
                              <div className="flex gap-1 mt-1">
                                {hostess.locations.includes("DOWNTOWN") && (
                                  <Badge variant="outline" className="text-xs">Downtown</Badge>
                                )}
                                {hostess.locations.includes("WEST_END") && (
                                  <Badge variant="outline" className="text-xs">West End</Badge>
                                )}
                              </div>
                            )}
                          </div>
                          <MessageCircle className="h-5 w-5 text-muted-foreground" />
                        </div>
                      </Card>
                    ))}
                </div>
              </div>

              {isLoadingConversations && (
                <div className="text-center py-8 text-muted-foreground">
                  Loading conversations...
                </div>
              )}
            </div>
          </div>
        ) : (
          /* Conversation View */
          <div className="flex-1 flex flex-col">
            {/* Conversation Header */}
            <div className="border-b p-4 flex items-center gap-3">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSelectedConversation(null)}
                data-testid="button-back"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <Avatar className="h-10 w-10">
                <AvatarImage src={selectedConversation?.hostess?.photoUrl || undefined} />
                <AvatarFallback>
                  {selectedConversation?.hostess?.displayName?.split(' ').map(n => n[0]).join('') || 'H'}
                </AvatarFallback>
              </Avatar>
              <div>
                <h2 className="font-semibold">{selectedConversation?.hostess?.displayName || 'Hostess'}</h2>
                {selectedConversation?.hostess?.locations && selectedConversation.hostess.locations.length > 0 && (
                  <div className="flex gap-1 mt-0.5">
                    {selectedConversation.hostess.locations.includes("DOWNTOWN") && (
                      <Badge variant="outline" className="text-xs">Downtown</Badge>
                    )}
                    {selectedConversation.hostess.locations.includes("WEST_END") && (
                      <Badge variant="outline" className="text-xs">West End</Badge>
                    )}
                  </div>
                )}
              </div>
            </div>

            {/* Messages */}
            <ScrollArea className="flex-1 p-4">
              {isLoadingMessages ? (
                <div className="text-center py-8 text-muted-foreground">
                  Loading messages...
                </div>
              ) : messages.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No messages yet. Start the conversation!
                </div>
              ) : (
                <div className="space-y-4 max-w-3xl mx-auto">
                  {messages.map((message) => {
                    const isOwnMessage = message.sender?.id === currentUserId;
                    return (
                      <div
                        key={message.id}
                        className={`flex ${isOwnMessage ? "justify-end" : "justify-start"}`}
                        data-testid={`message-${message.id}`}
                      >
                        <div
                          className={`max-w-[70%] rounded-lg p-3 ${
                            isOwnMessage
                              ? "bg-primary text-primary-foreground"
                              : "bg-muted"
                          }`}
                        >
                          <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
                          <p className={`text-xs mt-1 ${isOwnMessage ? "text-primary-foreground/70" : "text-muted-foreground"}`}>
                            {format(new Date(message.createdAt), "h:mm a")}
                          </p>
                        </div>
                      </div>
                    );
                  })}
                  <div ref={messagesEndRef} />
                </div>
              )}
            </ScrollArea>

            {/* Message Input */}
            <div className="border-t p-4">
              <div className="max-w-3xl mx-auto flex gap-2">
                <Input
                  value={messageInput}
                  onChange={(e) => setMessageInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter" && !e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                  placeholder="Type a message..."
                  disabled={sendMessageMutation.isPending}
                  data-testid="input-message"
                />
                <Button
                  onClick={handleSendMessage}
                  disabled={!messageInput.trim() || sendMessageMutation.isPending}
                  data-testid="button-send"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin-flagged-conversat.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { AlertTriangle, MessageSquare, CheckCircle, Eye } from "lucide-react";
import { format } from "date-fns";
import type { FlaggedConversationWithDetails, MessageWithSender } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export default function AdminFlaggedConversations() {
  const { toast } = useToast();
  const [selectedFlagged, setSelectedFlagged] = useState<FlaggedConversationWithDetails | null>(null);
  const [viewTab, setViewTab] = useState<"unreviewed" | "reviewed">("unreviewed");

  // Fetch flagged conversations
  const { data: unreviewedFlags = [], isLoading: isLoadingUnreviewed } = useQuery<FlaggedConversationWithDetails[]>({
    queryKey: ["/api/admin/flagged-conversations?reviewed=false"],
  });

  const { data: reviewedFlags = [], isLoading: isLoadingReviewed } = useQuery<FlaggedConversationWithDetails[]>({
    queryKey: ["/api/admin/flagged-conversations?reviewed=true"],
  });

  // Fetch messages for selected conversation
  const { data: messages = [] } = useQuery<MessageWithSender[]>({
    queryKey: ["/api/conversations", selectedFlagged?.conversationId, "messages"],
    enabled: !!selectedFlagged,
  });

  // Mark as reviewed mutation
  const markAsReviewedMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("PATCH", `/api/admin/flagged-conversations/${id}/review`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/flagged-conversations?reviewed=false"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/flagged-conversations?reviewed=true"] });
      setSelectedFlagged(null);
      toast({
        title: "Success",
        description: "Conversation marked as reviewed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to mark conversation as reviewed.",
        variant: "destructive",
      });
    },
  });

  const handleMarkAsReviewed = (id: string) => {
    markAsReviewedMutation.mutate(id);
  };

  const FlaggedTable = ({ flags, isLoading }: { flags: FlaggedConversationWithDetails[]; isLoading: boolean }) => {
    if (isLoading) {
      return (
        <div className="text-center py-8 text-muted-foreground">
          Loading flagged conversations...
        </div>
      );
    }

    if (flags.length === 0) {
      return (
        <div className="text-center py-8 text-muted-foreground">
          No flagged conversations.
        </div>
      );
    }

    return (
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Triggered Word</TableHead>
            <TableHead>Client</TableHead>
            <TableHead>Hostess</TableHead>
            <TableHead>Flagged Date</TableHead>
            <TableHead>Status</TableHead>
            <TableHead className="text-right">Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {flags.map((flag) => (
            <TableRow key={flag.id} data-testid={`flagged-${flag.id}`}>
              <TableCell>
                <Badge variant="destructive" className="font-mono">
                  {flag.triggeredWord}
                </Badge>
              </TableCell>
              <TableCell>{flag.conversation.client.email.split('@')[0]}</TableCell>
              <TableCell>{flag.conversation.hostess.displayName}</TableCell>
              <TableCell className="text-muted-foreground">
                {format(new Date(flag.flaggedAt), "MMM d, yyyy h:mm a")}
              </TableCell>
              <TableCell>
                {flag.reviewed ? (
                  <Badge variant="secondary">
                    <CheckCircle className="h-3 w-3 mr-1" />
                    Reviewed
                  </Badge>
                ) : (
                  <Badge variant="outline">
                    <AlertTriangle className="h-3 w-3 mr-1" />
                    Pending
                  </Badge>
                )}
              </TableCell>
              <TableCell className="text-right space-x-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSelectedFlagged(flag)}
                  data-testid={`button-view-${flag.id}`}
                >
                  <Eye className="h-4 w-4 mr-1" />
                  View
                </Button>
                {!flag.reviewed && (
                  <Button
                    variant="default"
                    size="sm"
                    onClick={() => handleMarkAsReviewed(flag.id)}
                    disabled={markAsReviewedMutation.isPending}
                    data-testid={`button-mark-reviewed-${flag.id}`}
                  >
                    <CheckCircle className="h-4 w-4 mr-1" />
                    Mark Reviewed
                  </Button>
                )}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    );
  };

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="flex items-center gap-3">
        <AlertTriangle className="h-8 w-8 text-destructive" />
        <div>
          <h1 className="text-3xl font-bold">Flagged Conversations</h1>
          <p className="text-muted-foreground mt-1">
            Review conversations that contain trigger words
          </p>
        </div>
      </div>

      {/* Stats */}
      <div className="grid gap-4 sm:grid-cols-2">
        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Pending Review</p>
              <h3 className="text-3xl font-bold mt-1">{unreviewedFlags.length}</h3>
            </div>
            <AlertTriangle className="h-10 w-10 text-destructive" />
          </div>
        </Card>
        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Total Reviewed</p>
              <h3 className="text-3xl font-bold mt-1">{reviewedFlags.length}</h3>
            </div>
            <CheckCircle className="h-10 w-10 text-green-600" />
          </div>
        </Card>
      </div>

      {/* Flagged Conversations List */}
      <Card>
        <Tabs value={viewTab} onValueChange={(v) => setViewTab(v as "unreviewed" | "reviewed")}>
          <div className="border-b p-4">
            <TabsList>
              <TabsTrigger value="unreviewed" data-testid="tab-unreviewed">
                Pending Review ({unreviewedFlags.length})
              </TabsTrigger>
              <TabsTrigger value="reviewed" data-testid="tab-reviewed">
                Reviewed ({reviewedFlags.length})
              </TabsTrigger>
            </TabsList>
          </div>

          <div className="p-6">
            <TabsContent value="unreviewed" className="mt-0">
              <FlaggedTable flags={unreviewedFlags} isLoading={isLoadingUnreviewed} />
            </TabsContent>

            <TabsContent value="reviewed" className="mt-0">
              <FlaggedTable flags={reviewedFlags} isLoading={isLoadingReviewed} />
            </TabsContent>
          </div>
        </Tabs>
      </Card>

      {/* Conversation View Dialog */}
      <Dialog open={!!selectedFlagged} onOpenChange={(open) => !open && setSelectedFlagged(null)}>
        <DialogContent className="max-w-3xl max-h-[80vh]">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <MessageSquare className="h-5 w-5" />
              Conversation Details
            </DialogTitle>
            <DialogDescription>
              {selectedFlagged && (
                <div className="space-y-2 mt-2">
                  <div className="flex items-center gap-2">
                    <span className="font-medium">Triggered Word:</span>
                    <Badge variant="destructive" className="font-mono">
                      {selectedFlagged.triggeredWord}
                    </Badge>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="font-medium">Between:</span>
                    <span>{selectedFlagged.conversation.client.email.split('@')[0]}</span>
                    <span className="text-muted-foreground">and</span>
                    <span>{selectedFlagged.conversation.hostess.displayName}</span>
                  </div>
                  <div>
                    <span className="font-medium">Flagged:</span>{" "}
                    {format(new Date(selectedFlagged.flaggedAt), "MMM d, yyyy 'at' h:mm a")}
                  </div>
                </div>
              )}
            </DialogDescription>
          </DialogHeader>

          {/* Messages */}
          <ScrollArea className="h-[400px] border rounded-lg p-4">
            {messages.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No messages in this conversation.
              </div>
            ) : (
              <div className="space-y-4">
                {messages.map((message) => {
                  const isClient = message.sender?.id === selectedFlagged?.conversation.clientId;
                  const isFlaggedMessage = message.id === selectedFlagged?.messageId;

                  return (
                    <div
                      key={message.id}
                      className={`${isFlaggedMessage ? "border-2 border-destructive rounded-lg p-2" : ""}`}
                      data-testid={`conversation-message-${message.id}`}
                    >
                      <div className={`flex ${isClient ? "justify-start" : "justify-end"}`}>
                        <div
                          className={`max-w-[70%] rounded-lg p-3 ${
                            isClient ? "bg-muted" : "bg-primary text-primary-foreground"
                          }`}
                        >
                          <div className="flex items-center gap-2 mb-1">
                            <span className="text-xs font-semibold">
                              {isClient 
                                ? selectedFlagged?.conversation.client.email.split('@')[0] 
                                : selectedFlagged?.conversation.hostess.displayName}
                            </span>
                            {isFlaggedMessage && (
                              <Badge variant="destructive" className="text-xs">Flagged</Badge>
                            )}
                          </div>
                          <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
                          <p className={`text-xs mt-1 ${isClient ? "text-muted-foreground" : "text-primary-foreground/70"}`}>
                            {format(new Date(message.createdAt), "h:mm a")}
                          </p>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </ScrollArea>

          {/* Actions */}
          {selectedFlagged && !selectedFlagged.reviewed && (
            <div className="flex justify-end gap-2">
              <Button
                onClick={() => handleMarkAsReviewed(selectedFlagged.id)}
                disabled={markAsReviewedMutation.isPending}
                data-testid="button-mark-reviewed-dialog"
              >
                <CheckCircle className="h-4 w-4 mr-2" />
                Mark as Reviewed
              </Button>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/staff-messages.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Send, ArrowLeft } from "lucide-react";
import { format } from "date-fns";
import type { ConversationWithDetails, MessageWithSender } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export default function StaffMessages() {
  const { toast } = useToast();
  const [selectedConversation, setSelectedConversation] = useState<ConversationWithDetails | null>(null);
  const [messageInput, setMessageInput] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Fetch conversations with real-time updates every 5 seconds
  const { data: conversations = [], isLoading: isLoadingConversations } = useQuery<ConversationWithDetails[]>({
    queryKey: ["/api/conversations"],
    refetchInterval: 5000, // Auto-refresh every 5 seconds
  });

  // Fetch messages for selected conversation with real-time updates
  const { data: messages = [], isLoading: isLoadingMessages } = useQuery<MessageWithSender[]>({
    queryKey: ["/api/conversations", selectedConversation?.id, "messages"],
    enabled: !!selectedConversation,
    refetchInterval: selectedConversation ? 3000 : false, // Auto-refresh every 3 seconds when viewing
  });

  // Mark conversation as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (conversationId: string) => {
      return apiRequest("POST", `/api/conversations/${conversationId}/read`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
    },
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      if (!selectedConversation) return;
      return apiRequest("POST", "/api/messages", {
        conversationId: selectedConversation.id,
        content,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations", selectedConversation?.id, "messages"] });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setMessageInput("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Mark conversation as read when selected
  useEffect(() => {
    if (selectedConversation) {
      markAsReadMutation.mutate(selectedConversation.id);
    }
  }, [selectedConversation?.id]);

  const handleSendMessage = () => {
    if (!messageInput.trim()) return;
    sendMessageMutation.mutate(messageInput);
  };

  // Get current user ID from first message sent by user
  const currentUserId = messages.find(m => m.sender)?.sender?.id;

  return (
    <div className="h-full flex flex-col bg-[hsl(220_15%_12%)]">
      <div className="border-b border-border p-4">
        <h1 className="text-2xl font-semibold text-foreground">Messages</h1>
        <p className="text-sm text-muted-foreground mt-1">
          Chat with your clients
        </p>
      </div>

      <div className="flex-1 flex overflow-hidden">
        {/* Conversations List */}
        {!selectedConversation ? (
          <div className="flex-1 p-4 overflow-auto">
            <div className="max-w-3xl mx-auto space-y-4">
              {conversations.length === 0 && !isLoadingConversations ? (
                <Card className="p-8 text-center">
                  <p className="text-muted-foreground">
                    No conversations yet. Clients will message you when they have questions.
                  </p>
                </Card>
              ) : (
                conversations.map((conversation) => (
                  <Card
                    key={conversation.id}
                    className="p-4 cursor-pointer hover-elevate active-elevate-2"
                    onClick={() => setSelectedConversation(conversation)}
                    data-testid={`conversation-${conversation.id}`}
                  >
                    <div className="flex items-center gap-3">
                      <Avatar className="h-12 w-12">
                        <AvatarFallback>
                          {conversation.client.username.substring(0, 2).toUpperCase()}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-2">
                            <h3 className="font-semibold truncate">
                              {conversation.client.username}
                            </h3>
                            {conversation.unreadCount && conversation.unreadCount > 0 && (
                              <Badge variant="default" className="text-xs px-2 py-0.5">
                                {conversation.unreadCount}
                              </Badge>
                            )}
                          </div>
                          {conversation.lastMessageAt && (
                            <span className="text-xs text-muted-foreground">
                              {format(new Date(conversation.lastMessageAt), "MMM d, h:mm a")}
                            </span>
                          )}
                        </div>
                        {conversation.lastMessage && (
                          <p className="text-sm text-muted-foreground truncate mt-1">
                            {conversation.lastMessage.content}
                          </p>
                        )}
                      </div>
                    </div>
                  </Card>
                ))
              )}

              {isLoadingConversations && (
                <div className="text-center py-8 text-muted-foreground">
                  Loading conversations...
                </div>
              )}
            </div>
          </div>
        ) : (
          /* Conversation View */
          <div className="flex-1 flex flex-col">
            {/* Conversation Header */}
            <div className="border-b border-border p-4 flex items-center gap-3">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSelectedConversation(null)}
                data-testid="button-back"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <Avatar className="h-10 w-10">
                <AvatarFallback>
                  {selectedConversation.client.username.substring(0, 2).toUpperCase()}
                </AvatarFallback>
              </Avatar>
              <div>
                <h2 className="font-semibold">{selectedConversation.client.username}</h2>
                <p className="text-xs text-muted-foreground">{selectedConversation.client.email}</p>
              </div>
            </div>

            {/* Messages */}
            <ScrollArea className="flex-1 p-4">
              {isLoadingMessages ? (
                <div className="text-center py-8 text-muted-foreground">
                  Loading messages...
                </div>
              ) : messages.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No messages yet. Start the conversation!
                </div>
              ) : (
                <div className="space-y-4 max-w-3xl mx-auto">
                  {messages.map((message) => {
                    const isOwnMessage = message.sender?.id === currentUserId;
                    return (
                      <div
                        key={message.id}
                        className={`flex ${isOwnMessage ? "justify-end" : "justify-start"}`}
                        data-testid={`message-${message.id}`}
                      >
                        <div
                          className={`max-w-[70%] rounded-lg p-3 ${
                            isOwnMessage
                              ? "bg-primary text-primary-foreground"
                              : "bg-card"
                          }`}
                        >
                          <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
                          <p className={`text-xs mt-1 ${isOwnMessage ? "text-primary-foreground/70" : "text-muted-foreground"}`}>
                            {format(new Date(message.createdAt), "h:mm a")}
                          </p>
                        </div>
                      </div>
                    );
                  })}
                  <div ref={messagesEndRef} />
                </div>
              )}
            </ScrollArea>

            {/* Message Input */}
            <div className="border-t border-border p-4">
              <div className="max-w-3xl mx-auto flex gap-2">
                <Input
                  value={messageInput}
                  onChange={(e) => setMessageInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter" && !e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                  placeholder="Type a message..."
                  disabled={sendMessageMutation.isPending}
                  data-testid="input-message"
                />
                <Button
                  onClick={handleSendMessage}
                  disabled={!messageInput.trim() || sendMessageMutation.isPending}
                  data-testid="button-send"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/client-upcoming-schedul.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Calendar, Phone, Info, ChevronLeft, ChevronRight, AlertCircle, MapPin, CalendarDays } from "lucide-react";
import { format, addDays, startOfWeek, endOfWeek, addWeeks, eachDayOfInterval } from "date-fns";
import type { UpcomingScheduleWithDetails } from "@shared/schema";

const SLOT_DURATION = 15; // minutes
const GRID_START = 10 * 60; // 10:00 AM in minutes
const GRID_END = 23 * 60; // 11:00 PM in minutes

type ViewMode = "daily" | "weekly";

function formatTimeRange(startMin: number, endMin: number): string {
  const formatTime = (min: number) => {
    const hours = Math.floor(min / 60);
    const minutes = min % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
  };
  return `${formatTime(startMin)}-${formatTime(endMin)}`;
}

export default function ClientUpcomingSchedule() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedLocation, setSelectedLocation] = useState<string>("all");
  const [viewMode, setViewMode] = useState<ViewMode>("daily");

  const dateStr = format(selectedDate, "yyyy-MM-dd");
  const weekStart = startOfWeek(selectedDate, { weekStartsOn: 0 });
  const weekEnd = endOfWeek(selectedDate, { weekStartsOn: 0 });
  const weekStartStr = format(weekStart, "yyyy-MM-dd");
  const weekEndStr = format(weekEnd, "yyyy-MM-dd");

  // Daily view query
  const { data: scheduleData = [], isLoading, error } = useQuery<UpcomingScheduleWithDetails[]>({
    queryKey: ["/api/upcoming-schedule", dateStr],
    queryFn: async () => {
      const response = await fetch(`/api/upcoming-schedule?startDate=${dateStr}&endDate=${dateStr}`);
      if (!response.ok) throw new Error("Failed to fetch schedule");
      return response.json();
    },
    enabled: viewMode === "daily",
  });

  // Weekly view query
  const { data: weeklyScheduleData = [], isLoading: isLoadingWeekly, error: errorWeekly } = useQuery<UpcomingScheduleWithDetails[]>({
    queryKey: ["/api/upcoming-schedule", "weekly", weekStartStr, weekEndStr],
    queryFn: async () => {
      const response = await fetch(`/api/upcoming-schedule?startDate=${weekStartStr}&endDate=${weekEndStr}`);
      if (!response.ok) throw new Error("Failed to fetch schedule");
      return response.json();
    },
    enabled: viewMode === "weekly",
  });

  // Filter schedule by location (daily)
  const filteredScheduleData = selectedLocation === "all" 
    ? scheduleData 
    : scheduleData.filter(s => s.hostess.locations?.includes(selectedLocation));

  // Filter schedule by location (weekly)
  const filteredWeeklyScheduleData = selectedLocation === "all" 
    ? weeklyScheduleData 
    : weeklyScheduleData.filter(s => s.hostess.locations?.includes(selectedLocation));

  // Get unique hostesses for this day (filtered by location)
  const hostesses = Array.from(
    new Set(filteredScheduleData.map(s => s.hostessId))
  ).map(id => {
    const schedule = filteredScheduleData.find(s => s.hostessId === id);
    return schedule?.hostess;
  }).filter(Boolean);

  // Get unique hostesses for the week (filtered by location)
  const weeklyHostesses = Array.from(
    new Set(filteredWeeklyScheduleData.map(s => s.hostessId))
  ).map(id => {
    const schedule = filteredWeeklyScheduleData.find(s => s.hostessId === id);
    return schedule?.hostess;
  }).filter(Boolean);

  // Get days of the week
  const weekDays = eachDayOfInterval({ start: weekStart, end: weekEnd });

  // Create time slots
  const timeSlots: number[] = [];
  for (let time = GRID_START; time < GRID_END; time += SLOT_DURATION) {
    timeSlots.push(time);
  }

  const goToPreviousDay = () => {
    setSelectedDate(prev => addDays(prev, -1));
  };

  const goToNextDay = () => {
    setSelectedDate(prev => addDays(prev, 1));
  };

  const goToPreviousWeek = () => {
    setSelectedDate(prev => addWeeks(prev, -1));
  };

  const goToNextWeek = () => {
    setSelectedDate(prev => addWeeks(prev, 1));
  };

  const goToToday = () => {
    setSelectedDate(new Date());
  };

  // Get schedule for a specific hostess and time (daily)
  const getScheduleAtTime = (hostessId: string, time: number) => {
    return filteredScheduleData.find(
      s => s.hostessId === hostessId && time >= s.startTime && time < s.endTime
    );
  };

  // Get schedules for a specific hostess and day (weekly)
  const getSchedulesForDay = (hostessId: string, date: Date) => {
    const dateStr = format(date, "yyyy-MM-dd");
    return filteredWeeklyScheduleData.filter(
      s => s.hostessId === hostessId && s.date === dateStr
    );
  };

  return (
    <div className="container mx-auto p-6 max-w-7xl">
      {/* Header */}
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2" data-testid="text-page-title">
          Upcoming Schedule Preview
        </h1>
        <p className="text-muted-foreground" data-testid="text-page-description">
          Preview next week's schedule. To book, please call Club Alpha.
        </p>
      </div>

      {/* Booking Notice */}
      <Alert className="mb-6 border-primary/50 bg-primary/5">
        <Phone className="h-4 w-4" />
        <AlertDescription className="text-sm">
          <strong>This is a preview schedule only.</strong> You cannot book appointments through the app for these dates. 
          Please call Club Alpha to reserve your preferred time slot.
        </AlertDescription>
      </Alert>

      {/* View Mode and Location Filter */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        {/* View Mode Toggle */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-4">
              <CalendarDays className="h-5 w-5 text-muted-foreground" />
              <div className="flex gap-2">
                <Button
                  variant={viewMode === "daily" ? "default" : "outline"}
                  onClick={() => setViewMode("daily")}
                  data-testid="button-view-daily"
                >
                  <Calendar className="h-4 w-4 mr-2" />
                  Daily
                </Button>
                <Button
                  variant={viewMode === "weekly" ? "default" : "outline"}
                  onClick={() => setViewMode("weekly")}
                  data-testid="button-view-weekly"
                >
                  <CalendarDays className="h-4 w-4 mr-2" />
                  Weekly
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Location Filter */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-4">
              <MapPin className="h-5 w-5 text-muted-foreground" />
              <div className="flex gap-2">
                <Button
                  variant={selectedLocation === "all" ? "default" : "outline"}
                  onClick={() => setSelectedLocation("all")}
                  data-testid="button-location-all"
                >
                  All Locations
                </Button>
                <Button
                  variant={selectedLocation === "DOWNTOWN" ? "default" : "outline"}
                  onClick={() => setSelectedLocation("DOWNTOWN")}
                  data-testid="button-location-downtown"
                >
                  Downtown
                </Button>
                <Button
                  variant={selectedLocation === "WEST_END" ? "default" : "outline"}
                  onClick={() => setSelectedLocation("WEST_END")}
                  data-testid="button-location-westend"
                >
                  West End
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Date Navigation */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="flex items-center justify-between gap-4">
            <Button
              variant="outline"
              size="icon"
              onClick={viewMode === "daily" ? goToPreviousDay : goToPreviousWeek}
              data-testid={viewMode === "daily" ? "button-previous-day" : "button-previous-week"}
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>

            <div className="flex items-center gap-4">
              <Calendar className="h-5 w-5 text-muted-foreground" />
              <div className="text-center">
                <h2 className="text-2xl font-bold" data-testid="text-selected-date">
                  {viewMode === "daily" 
                    ? format(selectedDate, "EEEE, MMMM d, yyyy")
                    : `${format(weekStart, "MMM d")} - ${format(weekEnd, "MMM d, yyyy")}`
                  }
                </h2>
              </div>
            </div>

            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={goToToday}
                data-testid="button-today"
              >
                Today
              </Button>
              <Button
                variant="outline"
                size="icon"
                onClick={viewMode === "daily" ? goToNextDay : goToNextWeek}
                data-testid={viewMode === "daily" ? "button-next-day" : "button-next-week"}
              >
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Schedule Grid */}
      {viewMode === "daily" ? (
        // DAILY VIEW
        error ? (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              Failed to load upcoming schedule. Please try again later or contact support if the problem persists.
            </AlertDescription>
          </Alert>
        ) : isLoading ? (
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground">Loading schedule...</p>
            </CardContent>
          </Card>
        ) : hostesses.length === 0 ? (
        <Card>
          <CardContent className="p-8 text-center">
            <Info className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-semibold mb-2">No Schedule Available</h3>
            <p className="text-muted-foreground">
              The schedule for this date hasn't been published yet.
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="overflow-auto border rounded-lg">
          <div className="inline-flex min-w-full">
            {/* Time Column */}
            <div className="w-20 flex-shrink-0 border-r bg-card sticky left-0 z-50 shadow-sm">
              <div className="h-16 border-b bg-card sticky top-0 z-50" />
              {timeSlots.map((slot) => (
                <div
                  key={slot}
                  className="h-12 border-b flex items-center justify-center text-sm text-muted-foreground font-mono"
                >
                  {formatTimeRange(slot, slot + SLOT_DURATION)}
                </div>
              ))}
            </div>

            {/* Hostess Columns */}
            {hostesses.map((hostess) => (
              <div 
                key={hostess!.id} 
                className="border-r flex-shrink-0 min-w-[200px]"
              >
                {/* Header */}
                <div className="h-16 border-b bg-card flex items-center justify-between px-3 sticky top-0 z-20">
                  <div className="flex items-center gap-2 flex-1 min-w-0">
                    <Avatar className="h-8 w-8">
                      <AvatarImage src={hostess!.photoUrl || undefined} />
                      <AvatarFallback className="text-xs">
                        {hostess!.displayName.split(' ').map(n => n[0]).join('')}
                      </AvatarFallback>
                    </Avatar>
                    <span className="text-sm truncate font-medium">
                      {hostess!.displayName}
                    </span>
                  </div>
                  {hostess!.locations && hostess!.locations.length > 0 && (
                    <div className="flex gap-0.5">
                      {hostess!.locations.includes("DOWNTOWN") && (
                        <Badge variant="outline" className="text-xs h-5 px-1">D</Badge>
                      )}
                      {hostess!.locations.includes("WEST_END") && (
                        <Badge variant="outline" className="text-xs h-5 px-1">W</Badge>
                      )}
                    </div>
                  )}
                </div>

                {/* Time Slots */}
                {timeSlots.map((slot) => {
                  const schedule = getScheduleAtTime(hostess!.id, slot);
                  const isFirstSlot = schedule && slot === schedule.startTime;
                  const slotsCount = schedule 
                    ? Math.ceil((schedule.endTime - schedule.startTime) / SLOT_DURATION)
                    : 0;

                  // Only render content on the first slot
                  if (schedule && !isFirstSlot) {
                    return <div key={slot} className="h-12 border-b" />;
                  }

                  return (
                    <div key={slot} className="h-12 border-b relative">
                      {isFirstSlot && (
                        <div 
                          className="absolute inset-0 bg-blue-500/20 border-l-2 border-blue-500 p-1 overflow-hidden"
                          style={{ height: `${slotsCount * 48}px` }}
                        >
                          <div className="text-xs font-semibold text-blue-700 dark:text-blue-300 mb-0.5">
                            {formatTimeRange(schedule.startTime, schedule.endTime)}
                          </div>
                          {schedule.service && (
                            <div className="text-xs text-blue-600 dark:text-blue-400 font-medium mb-0.5">
                              {schedule.service.name}
                            </div>
                          )}
                          {schedule.notes && (
                            <div className="text-xs text-muted-foreground italic">
                              {schedule.notes}
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        </div>
        )
      ) : (
        // WEEKLY VIEW
        errorWeekly ? (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              Failed to load upcoming schedule. Please try again later or contact support if the problem persists.
            </AlertDescription>
          </Alert>
        ) : isLoadingWeekly ? (
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground">Loading schedule...</p>
            </CardContent>
          </Card>
        ) : weeklyHostesses.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <Info className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
              <h3 className="text-lg font-semibold mb-2">No Schedule Available</h3>
              <p className="text-muted-foreground">
                The schedule for this week hasn't been published yet.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="overflow-auto border rounded-lg">
            <table className="w-full">
              <thead>
                <tr className="border-b bg-card">
                  <th className="p-3 text-left font-semibold min-w-[150px] sticky left-0 z-20 bg-card border-r">
                    Hostess
                  </th>
                  {weekDays.map((day) => (
                    <th key={day.toISOString()} className="p-3 text-center font-semibold min-w-[140px] border-r">
                      <div className="text-sm">
                        {format(day, "EEE")}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {format(day, "MMM d")}
                      </div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {weeklyHostesses.map((hostess) => (
                  <tr key={hostess!.id} className="border-b hover-elevate">
                    <td className="p-3 sticky left-0 z-10 bg-card border-r">
                      <div className="flex items-center gap-2">
                        <Avatar className="h-8 w-8">
                          <AvatarImage src={hostess!.photoUrl || undefined} />
                          <AvatarFallback className="text-xs">
                            {hostess!.displayName.split(' ').map(n => n[0]).join('')}
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex-1 min-w-0">
                          <div className="text-sm font-medium truncate">
                            {hostess!.displayName}
                          </div>
                          {hostess!.locations && hostess!.locations.length > 0 && (
                            <div className="flex gap-1 mt-0.5">
                              {hostess!.locations.includes("DOWNTOWN") && (
                                <Badge variant="outline" className="text-xs h-4 px-1">D</Badge>
                              )}
                              {hostess!.locations.includes("WEST_END") && (
                                <Badge variant="outline" className="text-xs h-4 px-1">W</Badge>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                    </td>
                    {weekDays.map((day) => {
                      const daySchedules = getSchedulesForDay(hostess!.id, day);
                      return (
                        <td key={day.toISOString()} className="p-2 border-r align-top">
                          {daySchedules.length > 0 ? (
                            <div className="space-y-1">
                              {daySchedules.map((schedule) => (
                                <div
                                  key={schedule.id}
                                  className="bg-blue-500/20 border border-blue-500/30 rounded p-1.5 text-xs"
                                  data-testid={`schedule-${hostess!.id}-${format(day, "yyyy-MM-dd")}`}
                                >
                                  <div className="font-semibold text-blue-700 dark:text-blue-300">
                                    {formatTimeRange(schedule.startTime, schedule.endTime)}
                                  </div>
                                  {schedule.service && (
                                    <div className="text-blue-600 dark:text-blue-400 font-medium mt-0.5">
                                      {schedule.service.name}
                                    </div>
                                  )}
                                  {schedule.notes && (
                                    <div className="text-muted-foreground italic mt-0.5">
                                      {schedule.notes}
                                    </div>
                                  )}
                                </div>
                              ))}
                            </div>
                          ) : (
                            <div className="text-xs text-muted-foreground text-center py-2">
                              -
                            </div>
                          )}
                        </td>
                      );
                    })}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )
      )}

      {/* Footer Info */}
      {(viewMode === "daily" ? hostesses.length > 0 : weeklyHostesses.length > 0) && (
        <Card className="mt-6">
          <CardHeader>
            <CardTitle className="text-lg flex items-center gap-2">
              <Phone className="h-5 w-5" />
              How to Book
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground">
              This is a preview of the upcoming schedule. To book any of the available time slots shown above, 
              please contact Club Alpha directly by phone. Our staff will confirm your appointment and provide 
              any additional information you may need.
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/admin-trigger-words.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { Plus, Trash2, Shield } from "lucide-react";
import { format } from "date-fns";
import type { TriggerWordWithDetails } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export default function AdminTriggerWords() {
  const { toast } = useToast();
  const [newWord, setNewWord] = useState("");
  const [deleteId, setDeleteId] = useState<string | null>(null);

  // Fetch trigger words
  const { data: triggerWords = [], isLoading } = useQuery<TriggerWordWithDetails[]>({
    queryKey: ["/api/admin/trigger-words"],
  });

  // Add trigger word mutation
  const addWordMutation = useMutation({
    mutationFn: async (word: string) => {
      return apiRequest("POST", "/api/admin/trigger-words", { word });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/trigger-words"] });
      setNewWord("");
      toast({
        title: "Success",
        description: "Trigger word added successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add trigger word.",
        variant: "destructive",
      });
    },
  });

  // Delete trigger word mutation
  const deleteWordMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/trigger-words/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/trigger-words"] });
      setDeleteId(null);
      toast({
        title: "Success",
        description: "Trigger word deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete trigger word.",
        variant: "destructive",
      });
    },
  });

  const handleAddWord = () => {
    if (!newWord.trim()) return;
    addWordMutation.mutate(newWord.trim());
  };

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      {/* Header */}
      <div className="flex items-center gap-3">
        <Shield className="h-8 w-8 text-primary" />
        <div>
          <h1 className="text-3xl font-bold">Trigger Words Management</h1>
          <p className="text-muted-foreground mt-1">
            Monitor conversations for specific words and phrases
          </p>
        </div>
      </div>

      {/* Add New Trigger Word */}
      <Card className="p-6">
        <h2 className="text-lg font-semibold mb-4">Add New Trigger Word</h2>
        <div className="flex gap-2">
          <Input
            value={newWord}
            onChange={(e) => setNewWord(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                handleAddWord();
              }
            }}
            placeholder="Enter word or phrase to monitor..."
            disabled={addWordMutation.isPending}
            data-testid="input-trigger-word"
          />
          <Button
            onClick={handleAddWord}
            disabled={!newWord.trim() || addWordMutation.isPending}
            data-testid="button-add-trigger-word"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add
          </Button>
        </div>
        <p className="text-xs text-muted-foreground mt-2">
          Words are case-insensitive. Conversations containing these words will be flagged for review.
        </p>
      </Card>

      {/* Trigger Words List */}
      <Card>
        <div className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold">Active Trigger Words</h2>
            <Badge variant="outline">{triggerWords.length} total</Badge>
          </div>

          {isLoading ? (
            <div className="text-center py-8 text-muted-foreground">
              Loading trigger words...
            </div>
          ) : triggerWords.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No trigger words configured. Add your first trigger word above.
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Word/Phrase</TableHead>
                  <TableHead>Added By</TableHead>
                  <TableHead>Date Added</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {triggerWords.map((tw) => (
                  <TableRow key={tw.id} data-testid={`trigger-word-${tw.id}`}>
                    <TableCell>
                      <Badge variant="secondary" className="font-mono">
                        {tw.word}
                      </Badge>
                    </TableCell>
                    <TableCell>{tw.addedByUser.email}</TableCell>
                    <TableCell className="text-muted-foreground">
                      {format(new Date(tw.createdAt), "MMM d, yyyy")}
                    </TableCell>
                    <TableCell className="text-right">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setDeleteId(tw.id)}
                        data-testid={`button-delete-${tw.id}`}
                      >
                        <Trash2 className="h-4 w-4 text-destructive" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </div>
      </Card>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={!!deleteId} onOpenChange={(open) => !open && setDeleteId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Trigger Word?</AlertDialogTitle>
            <AlertDialogDescription>
              This will remove the trigger word from monitoring. Existing flagged conversations will not be affected.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => deleteId && deleteWordMutation.mutate(deleteId)}
              data-testid="button-confirm-delete"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/admin-csv-splitter.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { FileUp, Download, Scissors, Info } from "lucide-react";

export default function AdminCSVSplitter() {
  const { toast } = useToast();
  const [csvData, setCsvData] = useState("");
  const [batches, setBatches] = useState<string[]>([]);
  const [totalRows, setTotalRows] = useState(0);

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target?.result as string;
        setCsvData(text);

        const lines = text.trim().split('\n');
        const rowCount = lines.length - 1; // Subtract header
        setTotalRows(rowCount);

        toast({
          title: "File loaded",
          description: `${rowCount.toLocaleString()} rows ready to split`,
        });
      };
      reader.readAsText(file);
    }
  };

  const splitCSV = () => {
    if (!csvData.trim()) {
      toast({
        variant: "destructive",
        title: "No CSV data",
        description: "Please upload a CSV file first",
      });
      return;
    }

    const lines = csvData.trim().split('\n');
    const header = lines[0];
    const dataRows = lines.slice(1);

    const BATCH_SIZE = 3000;
    const newBatches: string[] = [];

    for (let i = 0; i < dataRows.length; i += BATCH_SIZE) {
      const batchRows = dataRows.slice(i, i + BATCH_SIZE);
      const batchContent = [header, ...batchRows].join('\n');
      newBatches.push(batchContent);
    }

    setBatches(newBatches);

    toast({
      title: "CSV split complete",
      description: `Created ${newBatches.length} batches of up to ${BATCH_SIZE.toLocaleString()} rows each`,
    });
  };

  const downloadBatch = (batchIndex: number) => {
    const content = batches[batchIndex];
    const blob = new Blob([content], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `batch-${batchIndex + 1}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);

    toast({
      title: "Download started",
      description: `Batch ${batchIndex + 1} downloaded`,
    });
  };

  const downloadAllBatches = () => {
    batches.forEach((_, index) => {
      setTimeout(() => downloadBatch(index), index * 500);
    });

    toast({
      title: "Downloading all batches",
      description: `${batches.length} files will download shortly`,
    });
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">CSV Splitter</h1>
        <p className="text-muted-foreground">
          Split large CSV files into smaller batches for easier importing
        </p>
      </div>

      <div className="grid gap-6">
        <Alert>
          <Info className="h-4 w-4" />
          <AlertDescription>
            <strong>How it works:</strong>
            <br />
            â€¢ Upload your large CSV file (e.g., 10,884 emails)
            <br />
            â€¢ Click "Split CSV" to divide it into batches of 3,000 rows each
            <br />
            â€¢ Download each batch separately
            <br />
            â€¢ Import each batch one at a time using the "Import Clients" page
            <br />
            â€¢ Each 3,000-client batch imports in 2-3 minutes reliably
          </AlertDescription>
        </Alert>

        <Card>
          <CardHeader>
            <CardTitle>Upload Large CSV</CardTitle>
            <CardDescription>
              Upload your CSV file to split into manageable batches
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">
                Upload CSV File
              </label>
              <input
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                className="block w-full text-sm text-muted-foreground
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-primary file:text-primary-foreground
                  hover:file:bg-primary/90"
                data-testid="input-file-upload"
              />
              {totalRows > 0 && (
                <p className="text-sm text-muted-foreground mt-2">
                  <strong>{totalRows.toLocaleString()} rows</strong> loaded and ready to split
                </p>
              )}
            </div>

            {csvData && (
              <Button
                onClick={splitCSV}
                className="w-full"
                size="lg"
                data-testid="button-split"
              >
                <Scissors className="h-4 w-4 mr-2" />
                Split CSV into Batches (3,000 rows each)
              </Button>
            )}
          </CardContent>
        </Card>

        {batches.length > 0 && (
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Split Results</CardTitle>
                  <CardDescription>
                    {batches.length} batches created
                  </CardDescription>
                </div>
                <Button
                  variant="outline"
                  onClick={downloadAllBatches}
                  className="gap-2"
                  data-testid="button-download-all"
                >
                  <Download className="h-4 w-4" />
                  Download All Batches
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid gap-3">
                {batches.map((batch, index) => {
                  const rowCount = batch.split('\n').length - 1;
                  return (
                    <div
                      key={index}
                      className="flex items-center justify-between p-4 rounded-md border bg-card hover-elevate"
                    >
                      <div>
                        <p className="font-medium">
                          Batch {index + 1}
                        </p>
                        <p className="text-sm text-muted-foreground">
                          {rowCount.toLocaleString()} rows
                        </p>
                      </div>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => downloadBatch(index)}
                        className="gap-2"
                        data-testid={`button-download-batch-${index + 1}`}
                      >
                        <Download className="h-4 w-4" />
                        Download
                      </Button>
                    </div>
                  );
                })}
              </div>

              <Alert className="mt-6 border-green-500/50 bg-green-500/10">
                <Info className="h-4 w-4 text-green-600 dark:text-green-400" />
                <AlertDescription className="text-green-800 dark:text-green-200">
                  <strong>Next Steps:</strong>
                  <br />
                  1. Download each batch file
                  <br />
                  2. Go to the "Import Clients" page
                  <br />
                  3. Import Batch 1, wait for completion
                  <br />
                  4. Import Batch 2, wait for completion
                  <br />
                  5. Continue until all batches are imported
                  <br />
                  <br />
                  <strong>Each 3,000-client batch will take 2-3 minutes to import.</strong>
                </AlertDescription>
              </Alert>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

